contracts/IPriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IPriceOracle
 * @dev Enhanced oracle interface for production vAMM
 */
interface IPriceOracle {
    /**
     * @dev Returns the current price with 18 decimals
     */
    function getPrice() external view returns (uint256);

    /**
     * @dev Returns the current price with timestamp
     */
    function getPriceWithTimestamp() external view returns (uint256 price, uint256 timestamp);

    /**
     * @dev Checks if the oracle is active and healthy
     */
    function isActive() external view returns (bool);

    /**
     * @dev Returns the maximum acceptable price age in seconds
     */
    function getMaxPriceAge() external view returns (uint256);
}

contracts/IvAMM.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IvAMM
 * @dev Interface for the virtual Automated Market Maker with multiple position support
 */
interface IvAMM {
    struct Position {
        uint256 positionId; // Unique position identifier
        int256 size; // Position size in USD (always positive, direction determined by isLong)
        bool isLong; // True for long, false for short
        uint256 entryPrice;
        uint256 entryFundingIndex;
        uint256 lastInteractionTime;
        bool isActive; // Whether position is currently active
    }

    struct FundingState {
        int256 fundingRate; // Current funding rate (per hour)
        uint256 fundingIndex; // Cumulative funding index
        uint256 lastFundingTime;
        int256 premiumFraction; // Premium/discount relative to index price
    }

    /**
     * @dev Opens a new position
     */
    function openPosition(
        uint256 collateralAmount,
        bool isLong,
        uint256 leverage,
        uint256 minPrice,
        uint256 maxPrice
    ) external returns (uint256 positionId);

    /**
     * @dev Adds to an existing position
     */
    function addToPosition(
        uint256 positionId,
        uint256 collateralAmount,
        uint256 leverage,
        uint256 minPrice,
        uint256 maxPrice
    ) external returns (uint256 newSize);

    /**
     * @dev Closes a position partially or fully
     */
    function closePosition(
        uint256 positionId,
        uint256 sizeToClose,
        uint256 minPrice,
        uint256 maxPrice
    ) external returns (int256 pnl);

    /**
     * @dev Gets current mark price
     */
    function getMarkPrice() external view returns (uint256);

    /**
     * @dev Gets current funding rate
     */
    function getFundingRate() external view returns (int256);

    /**
     * @dev Updates funding rate
     */
    function updateFunding() external;

    /**
     * @dev Gets position data by position ID
     */
    function getPosition(uint256 positionId) external view returns (Position memory);

    /**
     * @dev Gets position data by user and position ID
     */
    function getUserPosition(address user, uint256 positionId) external view returns (Position memory);

    /**
     * @dev Gets all active positions for a user
     */
    function getUserPositions(address user) external view returns (Position[] memory);

    /**
     * @dev Gets all active position IDs for a user
     */
    function getUserPositionIds(address user) external view returns (uint256[] memory);

    /**
     * @dev Gets funding state
     */
    function getFundingState() external view returns (FundingState memory);

    /**
     * @dev Calculates unrealized PnL for a specific position
     */
    function getUnrealizedPnL(uint256 positionId) external view returns (int256);

    /**
     * @dev Calculates total unrealized PnL for a user across all positions
     */
    function getTotalUnrealizedPnL(address user) external view returns (int256);

    /**
     * @dev Gets the price impact for a trade
     */
    function getPriceImpact(uint256 size, bool isLong) external view returns (uint256);

    /**
     * @dev Gets user's trading summary
     */
    function getUserSummary(address user) external view returns (
        uint256 totalLongSize,
        uint256 totalShortSize,
        int256 totalPnL,
        uint256 activePositionsCount
    );

    /**
     * @dev Pauses all trading operations
     */
    function pause() external;

    /**
     * @dev Resumes all trading operations  
     */
    function unpause() external;

    /**
     * @dev Gets position data by user address (legacy function)
     */
    function getPosition(address user) external view returns (Position memory);

    /**
     * @dev Gets unrealized PnL by user address (legacy function)
     */
    function getUnrealizedPnL(address user) external view returns (int256);
}

contracts/IVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IVault
 * @dev Interface for the margin vault contract
 */
interface IVault {
    struct MarginAccount {
        uint256 collateral;
        uint256 reservedMargin;
        int256 unrealizedPnL;
        uint256 lastFundingIndex;
    }

    /**
     * @dev Deposits collateral for a user
     */
    function depositCollateral(address user, uint256 amount) external;

    /**
     * @dev Withdraws collateral for a user
     */
    function withdrawCollateral(address user, uint256 amount) external;

    /**
     * @dev Reserves margin for a position
     */
    function reserveMargin(address user, uint256 amount) external;

    /**
     * @dev Releases reserved margin
     */
    function releaseMargin(address user, uint256 amount) external;

    /**
     * @dev Updates PnL for a user
     */
    function updatePnL(address user, int256 pnlDelta) external;

    /**
     * @dev Applies funding payment
     */
    function applyFunding(address user, int256 fundingPayment, uint256 fundingIndex) external;

    /**
     * @dev Gets available margin for a user
     */
    function getAvailableMargin(address user) external view returns (uint256);

    /**
     * @dev Gets total margin (collateral + unrealized PnL)
     */
    function getTotalMargin(address user) external view returns (int256);

    /**
     * @dev Checks if user can be liquidated
     */
    function canLiquidate(address user, uint256 maintenanceMarginRatio) external view returns (bool);

    /**
     * @dev Liquidates a user's position
     */
    function liquidate(address user, uint256 liquidationPenalty) external;

    /**
     * @dev Gets user's margin account
     */
    function getMarginAccount(address user) external view returns (MarginAccount memory);
}

contracts/MockPriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IPriceOracle.sol";

/**
 * @title MockPriceOracle
 * @dev Mock price oracle for testing and development
 */
contract MockPriceOracle is IPriceOracle {
    address public owner;
    uint256 private price;
    uint256 private lastUpdateTime;
    uint256 public maxPriceAge = 1 hours;
    bool public active = true;

    event PriceUpdated(uint256 newPrice, uint256 timestamp);
    event OracleStatusChanged(bool active);
    event MaxPriceAgeUpdated(uint256 newAge);

    modifier onlyOwner() {
        require(msg.sender == owner, "Oracle: not owner");
        _;
    }

    constructor(uint256 _initialPrice) {
        owner = msg.sender;
        price = _initialPrice;
        lastUpdateTime = block.timestamp;
    }

    /**
     * @dev Returns the current price with 18 decimals
     */
    function getPrice() external view override returns (uint256) {
        require(active, "Oracle: inactive");
        require(block.timestamp <= lastUpdateTime + maxPriceAge, "Oracle: price too old");
        return price;
    }

    /**
     * @dev Returns the current price with timestamp
     */
    function getPriceWithTimestamp() external view override returns (uint256, uint256) {
        require(active, "Oracle: inactive");
        return (price, lastUpdateTime);
    }

    /**
     * @dev Checks if the oracle is active and healthy
     */
    function isActive() external view override returns (bool) {
        return active && (block.timestamp <= lastUpdateTime + maxPriceAge);
    }

    /**
     * @dev Returns the maximum acceptable price age in seconds
     */
    function getMaxPriceAge() external view override returns (uint256) {
        return maxPriceAge;
    }

    /**
     * @dev Updates the price (admin only)
     */
    function updatePrice(uint256 _newPrice) external onlyOwner {
        require(_newPrice > 0, "Oracle: invalid price");
        price = _newPrice;
        lastUpdateTime = block.timestamp;
        emit PriceUpdated(_newPrice, block.timestamp);
    }

    /**
     * @dev Sets oracle active status
     */
    function setActive(bool _active) external onlyOwner {
        active = _active;
        emit OracleStatusChanged(_active);
    }

    /**
     * @dev Updates max price age
     */
    function setMaxPriceAge(uint256 _maxAge) external onlyOwner {
        require(_maxAge > 0, "Oracle: invalid age");
        maxPriceAge = _maxAge;
        emit MaxPriceAgeUpdated(_maxAge);
    }

    /**
     * @dev Simulates price movement (for testing)
     */

    function simulatePriceMovement(int256 changePercent) external onlyOwner {
        require(changePercent >= -5000 && changePercent <= 5000, "Oracle: change too large"); // Max 50% change

        int256 newPrice = int256(price) + (int256(price) * changePercent) / 10000;
        require(newPrice > 0, "Oracle: invalid result");

        price = uint256(newPrice);
        lastUpdateTime = block.timestamp;
        emit PriceUpdated(price, block.timestamp);
    }

    /**
     * @dev Transfers ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Oracle: invalid owner");
        owner = newOwner;
    }
}

contracts/MockUSDC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title MockUSDC
 * @dev Mock USDC token for testing vAMM system
 */
contract MockUSDC {
    string public name = "Mock USD Coin";
    string public symbol = "USDC";
    uint8 public decimals = 6; // USDC has 6 decimals
    uint256 public totalSupply;

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    address public owner;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event Mint(address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "USDC: not owner");
        _;
    }

    constructor(uint256 _initialSupply) {
        owner = msg.sender;
        totalSupply = _initialSupply * 10**decimals;
        balanceOf[msg.sender] = totalSupply;
        emit Transfer(address(0), msg.sender, totalSupply);
    }

    function transfer(address to, uint256 value) external returns (bool) {
        require(to != address(0), "USDC: invalid recipient");
        require(balanceOf[msg.sender] >= value, "USDC: insufficient balance");

        balanceOf[msg.sender] -= value;
        balanceOf[to] += value;
        emit Transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) external returns (bool) {
        require(to != address(0), "USDC: invalid recipient");
        require(balanceOf[from] >= value, "USDC: insufficient balance");
        require(allowance[from][msg.sender] >= value, "USDC: insufficient allowance");

        balanceOf[from] -= value;
        balanceOf[to] += value;
        allowance[from][msg.sender] -= value;

        emit Transfer(from, to, value);
        return true;
    }

    function approve(address spender, uint256 value) external returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function mint(address to, uint256 value) external onlyOwner {
        require(to != address(0), "USDC: invalid recipient");

        totalSupply += value;
        balanceOf[to] += value;
        emit Mint(to, value);
        emit Transfer(address(0), to, value);
    }

    function faucet(uint256 amount) external {
        require(amount <= 10000 * 10**decimals, "USDC: faucet limit exceeded"); // Max 10,000 USDC

        totalSupply += amount;
        balanceOf[msg.sender] += amount;
        emit Mint(msg.sender, amount);
        emit Transfer(address(0), msg.sender, amount);
    }

    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "USDC: invalid owner");
        owner = newOwner;
    }
}

contracts/PRBMath.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/// @title PRBMath
/// @author Paul Razvan Berg
/// @notice A gas-efficient and safe Solidity library for binary fixed-point arithmetic.
/// @dev The math is overflowing-safe.
library PRBMath {
    /**
     * @dev Emitted when the result of a multiplication overflows.
     * @param result The supposedly correct result.
     */
    error MulOverflow(uint256 result);

    /**
     * @dev Returns the most significant bit of a number.
     * @param x The number to check.
     * @return msb The most significant bit of the number.
     */
    function mostSignificantBit(uint256 x) internal pure returns (uint256 msb) {
        if (x >= 2**128) {
            x >>= 128;
            msb += 128;
        }
        if (x >= 2**64) {
            x >>= 64;
            msb += 64;
        }
        if (x >= 2**32) {
            x >>= 32;
            msb += 32;
        }
        if (x >= 2**16) {
            x >>= 16;
            msb += 16;
        }
        if (x >= 2**8) {
            x >>= 8;
            msb += 8;
        }
        if (x >= 2**4) {
            x >>= 4;
            msb += 4;
        }
        if (x >= 2**2) {
            x >>= 2;
            msb += 2;
        }
        if (x >= 2**1) {
            msb += 1;
        }
    }

    /**
     * @dev Calculates the integer square root of a number.
     * @param x The number to square root.
     * @return result The integer square root of the number.
     */
    function sqrt(uint256 x) internal pure returns (uint256 result) {
        if (x == 0) {
            return 0;
        }

        // The following code is ported from the Babylonian method for computing the square root of a number.
        // See https://en.wikipedia.org/wiki/Methods_of_computing_square_roots#Babylonian_method
        result = 1 << (mostSignificantBit(x) >> 1);
        result = (result + x / result) >> 1;
        result = (result + x / result) >> 1;
        result = (result + x / result) >> 1;
        result = (result + x / result) >> 1;
        result = (result + x / result) >> 1;
        result = (result + x / result) >> 1;
        result = (result + x / result) >> 1;
        result = (result + x / result) >> 1;

        // The operations above might have rounded down the result. If that is the case, we increment the result.
        uint256 squared = result * result;
        if (squared < x) {
            result++;
        }

        // If the next squared number is still lesser than or equal to x, that means we haven't reached the closest
        // integer square root and we need to increment the result again.
        if ((result * result) <= x) {
            // This is a branch that should not be reachable.
        } else {
            result--;
        }
    }
}

contracts/PRBMathUD60x18.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import { PRBMath } from "./PRBMath.sol";

/// @title PRBMathUD60x18
/// @author Paul Razvan Berg
/// @notice A library for working with unsigned 60.18-decimal fixed-point numbers.
/// @dev Numbers are stored in uint256 slots as unsigned 60.18-decimal fixed-point numbers.
library PRBMathUD60x18 {
    /// @dev Emitted when an input is not a number.
    error NotANumber();

    /// @dev The constant number one as a UD60x18.
    uint256 internal constant ONE = 1e18;

    /// @dev The constant pi as a UD60x18.
    uint256 internal constant PI = 3141592653589793238;

    /// @dev The maximum value for a UD60x18.
    uint256 internal constant MAX_UD60x18 =
        115792089237316195423570985008687907853269984665640564039457_584007913129639935;

    /// @dev The maximum whole value for a UD60x18.
    uint256 internal constant MAX_WHOLE_UD60x18 = MAX_UD60x18 - (MAX_UD60x18 % ONE);

    /// @notice Adds two UD60x18 numbers.
    /// @param x A UD60x18.
    /// @param y A UD60x18.
    /// @return result The sum of x and y.
    function add(uint256 x, uint256 y) internal pure returns (uint256 result) {
        unchecked {
            result = x + y;
            if (result < x) {
                revert PRBMath.MulOverflow(result);
            }
        }
    }

    /// @notice Returns the average of two UD60x18 numbers.
    /// @param x A UD60x18.
    /// @param y A UD60x18.
    /// @return result The average of x and y.
    function avg(uint256 x, uint256 y) internal pure returns (uint256 result) {
        // The sum of x and y won't overflow if their average is smaller than the max UD60x18.
        result = (x & y) + ((x ^ y) >> 1);
    }

    /// @notice Returns the smallest power of two that is greater than or equal to the given UD60x18.
    /// @param x A UD60x18.
    /// @return result The smallest power of two that is greater than or equal to x.
    function ceil(uint256 x) internal pure returns (uint256 result) {
        if (x > MAX_WHOLE_UD60x18) {
            revert PRBMath.MulOverflow(x);
        }
        uint256 remainder = x % ONE;
        if (remainder == 0) {
            result = x;
        } else {
            result = (x - remainder) + ONE;
        }
    }

    /// @notice Divides two UD60x18 numbers.
    /// @param x The dividend as a UD60x18.
    /// @param y The divisor as a UD60x18.
    /// @return result The quotient of x divided by y.
    function div(uint256 x, uint256 y) internal pure returns (uint256 result) {
        if (y == 0) {
            revert PRBMath.MulOverflow(0);
        }
        uint256 z = x * ONE;
        // The following check is based on the fact that z / y >= x is equivalent to z >= x * y.
        if (x != 0 && z / x != ONE) {
            revert PRBMath.MulOverflow(z);
        }
        result = z / y;
    }

    /// @notice Returns Euler's number as a UD60x18.
    function e() internal pure returns (uint256 result) {
        result = 2718281828459045235;
    }

    /// @notice Calculates the x-th power of Euler's number.
    /// @param x A UD60x18.
    /// @return result The x-th power of Euler's number.
    function exp(uint256 x) internal pure returns (uint256 result) {
        if (x > 135305999368893231589) {
            revert PRBMath.MulOverflow(x);
        }

        // When the exponent is 0, the result is 1.
        if (x == 0) {
            return ONE;
        }

        // The order of the Taylor series.
        uint256 n = 12;

        // The first term of the series is 1.
        result = ONE;

        // The second term of the series is x.
        uint256 term = x;

        // Calculate the remaining terms of the series.
        for (uint256 i = 2; i <= n; i++) {
            term = div(mul(term, x), fromUint(i));
            result += term;
        }
    }

    /// @notice Calculates the x-th power of 2.
    /// @param x The exponent as an integer.
    /// @return result The x-th power of 2.
    function exp2(uint256 x) internal pure returns (uint256 result) {
        if (x >= 199) {
            revert PRBMath.MulOverflow(x);
        }
        result = 1 << x;
    }

    /// @notice Returns the largest integer that is less than or equal to the given UD60x18.
    /// @param x A UD60x18.
    /// @return result The largest integer that is less than or equal to x.
    function floor(uint256 x) internal pure returns (uint256 result) {
        result = x - (x % ONE);
    }

    /// @notice Returns the fractional part of a UD60x18.
    /// @param x A UD60x18.
    /// @return result The fractional part of x.
    function frac(uint256 x) internal pure returns (uint256 result) {
        result = x % ONE;
    }

    /// @notice Converts a uint256 to a UD60x18.
    /// @param x A uint256.
    /// @return result The UD60x18 representation of x.
    function fromUint(uint256 x) internal pure returns (uint256 result) {
        if (x > MAX_UD60x18 / ONE) {
            revert PRBMath.MulOverflow(x);
        }
        result = x * ONE;
    }

    /// @notice Returns the geometric mean of two UD60x18 numbers.
    /// @param x A UD60x18.
    /// @param y A UD60x18.
    /// @return result The geometric mean of x and y.
    function gm(uint256 x, uint256 y) internal pure returns (uint256 result) {
        uint256 xy = x * y;

        // The following check is based on the fact that xy / x == y is equivalent to xy == x * y.
        if (x != 0 && xy / x != y) {
            revert PRBMath.MulOverflow(xy);
        }

        // We don't need to use the "fromUint" helper function because the result of the multiplication is
        // already a UD60x18.
        result = PRBMath.sqrt(xy);
    }

    /// @notice Returns the inverse of a UD60x18.
    /// @param x A UD60x18.
    /// @return result 1/x.
    function inv(uint256 x) internal pure returns (uint256 result) {
        if (x == 0) {
            revert PRBMath.MulOverflow(0);
        }
        result = (ONE * ONE) / x;
    }

    /// @notice Calculates the natural logarithm of a UD60x18.
    /// @param x A UD60x18.
    /// @return result The natural logarithm of x.
    function log(uint256 x) internal pure returns (uint256 result) {
        if (x == 0) {
            revert NotANumber();
        }
        // The following code is ported from the iterative formula for the natural logarithm.
        // See https://en.wikipedia.org/wiki/Natural_logarithm#High_precision
        if (x < ONE) {
            uint256 z = div(sub(ONE, x), add(ONE, x));
            uint256 z2 = mul(z, z);
            uint256 n = 1;
            uint256 term = z;
            result = term;
            for (uint256 i = 1; i < 5; i++) {
                n += 2;
                term = mul(term, z2);
                result += div(term, fromUint(n));
            }
            result = 0 - (result << 1);
        } else {
            uint256 y = div(sub(x, ONE), add(x, ONE));
            uint256 y2 = mul(y, y);
            uint256 n = 1;
            uint256 term = y;
            result = term;
            for (uint256 i = 1; i < 5; i++) {
                n += 2;
                term = mul(term, y2);
                result += div(term, fromUint(n));
            }
            result = result << 1;
        }
    }

    /// @notice Calculates the base-10 logarithm of a UD60x18.
    /// @param x A UD60x18.
    /// @return result The base-10 logarithm of x.
    function log10(uint256 x) internal pure returns (uint256 result) {
        // ln(10) in UD60x18.
        uint256 ln10 = 2302585092994045684;
        result = div(log(x), ln10);
    }

    /// @notice Calculates the base-2 logarithm of a UD60x18.
    /// @param x A UD60x18.
    /// @return result The base-2 logarithm of x.
    function log2(uint256 x) internal pure returns (uint256 result) {
        if (x == 0) {
            revert NotANumber();
        }
        // The following code is ported from the iterative formula for the base-2 logarithm.
        // See https://math.stackexchange.com/a/2464010
        int256 msb = int256(PRBMath.mostSignificantBit(x / ONE));
        if (msb < 0) {
            uint256 invX = inv(x);
            msb = int256(PRBMath.mostSignificantBit(invX / ONE));
            uint256 b = fromUint(uint256(msb));
            result = mul(invX, exp2(uint256(msb)));
            for (uint256 i = 0; i < 4; i++) {
                result = mul(result, result);
            }
            result = 0 - sub(b, sub(fromUint(1), result));
        } else {
            uint256 b = fromUint(uint256(msb));
            result = div(x, exp2(uint256(msb)));
            for (uint256 i = 0; i < 4; i++) {
                result = mul(result, result);
            }
            result = add(b, sub(fromUint(1), result));
        }
    }

    /// @notice Returns the greater of two numbers.
    /// @param x A UD60x18.
    /// @param y A UD60x18.
    /// @return result The greater of x and y.
    function max(uint256 x, uint256 y) internal pure returns (uint256 result) {
        result = x > y ? x : y;
    }

    /// @notice Returns the smaller of two numbers.
    /// @param x A UD60x18.
    /// @param y A UD60x18.
    /// @return result The smaller of x and y.
    function min(uint256 x, uint256 y) internal pure returns (uint256 result) {
        result = x < y ? x : y;
    }

    /// @notice Returns the modulo of two UD60x18 numbers.
    /// @param x A UD60x18.
    /// @param y A UD60x18.
    /// @return result The result of x % y.
    function mod(uint256 x, uint256 y) internal pure returns (uint256 result) {
        if (y == 0) {
            revert PRBMath.MulOverflow(0);
        }
        result = x % y;
    }

    /// @notice Multiplies two UD60x18 numbers.
    /// @param x The multiplicand as a UD60x18.
    /// @param y The multiplier as a UD60x18.
    /// @return result The product of x times y.
    function mul(uint256 x, uint256 y) internal pure returns (uint256 result) {
        uint256 z = x * y;
        // The following check is based on the fact that z / x == y is equivalent to z == x * y.
        if (x != 0 && z / x != y) {
            revert PRBMath.MulOverflow(z);
        }
        result = z;
    }

    /// @notice Returns pi as a UD60x18.
    function pi() internal pure returns (uint256 result) {
        result = PI;
    }

    /// @notice Raises a UD60x18 to the power of another UD60x18.
    /// @param x The base as a UD60x18.
    /// @param y The exponent as a UD60x18.
    /// @return result The result of x raised to the power of y.
    function pow(uint256 x, uint256 y) internal pure returns (uint256 result) {
        if (x == 0) {
            result = y == 0 ? ONE : 0;
        } else {
            result = exp(mul(y, log(x)));
        }
    }

    /// @notice Raises a UD60x18 to the power of an integer.
    /// @param x The base as a UD60x18.
    /// @param y The exponent as an integer.
    /// @return result The result of x raised to the power of y.
    function powu(uint256 x, uint256 y) internal pure returns (uint256 result) {
        if (x == 0) {
            return y == 0 ? ONE : 0;
        }
        if (y == 0) {
            return ONE;
        }

        result = ONE;
        uint256 xPower = x;

        // The following code is ported from the exponentiation by squaring algorithm.
        // See https://en.wikipedia.org/wiki/Exponentiation_by_squaring
        while (y > 0) {
            if (y % 2 == 1) {
                result = mul(result, xPower);
            }
            xPower = mul(xPower, xPower);
            y /= 2;
        }
    }

    /// @notice Returns the given UD60x18 rounded to the closest integer.
    /// @param x A UD60x18.
    /// @return result The given UD60x18 rounded to the closest integer.
    function round(uint256 x) internal pure returns (uint256 result) {
        uint256 remainder = x % ONE;
        uint256 half = 0.5e18;
        if (remainder < half) {
            result = floor(x);
        } else {
            result = ceil(x);
        }
    }

    /// @notice Returns the square root of a UD60x18.
    /// @param x A UD60x18.
    /// @return result The square root of x.
    function sqrt(uint256 x) internal pure returns (uint256 result) {
        if (x > 0) {
            uint256 xTimesOne = x * ONE;
            if (xTimesOne / x != ONE) {
                revert PRBMath.MulOverflow(xTimesOne);
            }
            result = PRBMath.sqrt(xTimesOne);
        }
    }

    /// @notice Subtracts two UD60x18 numbers.
    /// @param x A UD60x18.
    /// @param y A UD60x18.
    /// @return result The difference of x and y.
    function sub(uint256 x, uint256 y) internal pure returns (uint256 result) {
        result = x - y;
        if (result > x) {
            revert PRBMath.MulOverflow(result);
        }
    }

    /// @notice Converts a UD60x18 to a uint256.
    /// @param x A UD60x18.
    /// @return result The uint256 representation of x.
    function toUint(uint256 x) internal pure returns (uint256 result) {
        result = x / ONE;
    }
}

contracts/vAMM.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IvAMM.sol";
import "./IVault.sol";
import "./IPriceOracle.sol";
import "./PRBMathUD60x18.sol";

/**
 * @title vAMM
 * @dev Production-ready virtual Automated Market Maker with BONDING CURVE mechanism for pump.fund-style behavior
 * This version uses bonding curves instead of traditional AMM reserves for custom starting prices and progressive difficulty
 */
contract vAMM is IvAMM {
    using PRBMathUD60x18 for uint256;

    address public owner;
    IVault public vault;
    IPriceOracle public oracle;

    // Trading parameters
    uint256 public constant PRICE_PRECISION = 1e18;
    uint256 public constant FUNDING_PRECISION = 1e8;
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant MAX_LEVERAGE = 100;
    uint256 public constant MIN_LEVERAGE = 1;

    // Fee structure
    uint256 public tradingFeeRate = 30; // 0.3% in basis points
    uint256 public liquidationFeeRate = 500; // 5% in basis points
    uint256 public maintenanceMarginRatio = 500; // 5% in basis points
    uint256 public initialMarginRatio = 1000; // 10% in basis points

    // ===== BONDING CURVE PARAMETERS =====
    uint256 public startingPrice; // Custom starting price (e.g., $0.001, $8, $100)
    uint256 public constant BONDING_CURVE_STEEPNESS = 1000; // Lower = easier pumps, Higher = harder pumps
    uint256 public constant MAX_PUMP_MULTIPLIER = 10000; // Maximum 10,000x price increase
    uint256 public pumpExponent = 15e17; // 1.5 exponent for progressive difficulty

    // Legacy virtual reserves for backwards compatibility (now calculated dynamically)
    uint256 public virtualBaseReserves;
    uint256 public virtualQuoteReserves;

    // Funding mechanism
    FundingState public fundingState;
    uint256 public constant FUNDING_INTERVAL = 1 hours;
    uint256 public constant MAX_FUNDING_RATE = 1e6; // 1% per hour in FUNDING_PRECISION

    // Multiple position tracking
    mapping(uint256 => Position) public positions; // positionId => Position
    mapping(address => uint256[]) public userPositionIds; // user => positionId[]
    mapping(uint256 => address) public positionOwner; // positionId => owner
    mapping(address => uint256) public nextPositionId; // user => next available position ID
    uint256 public globalPositionId; // Global position counter
    mapping(address => bool) public authorized;

    // Global state tracking for bonding curve
    int256 public totalLongSize; // Total long positions (drives bonding curve)
    int256 public totalShortSize; // Total short positions
    uint256 public totalTradingFees;
    bool public paused = false;

    // Events
    event PositionOpened(
        address indexed user,
        uint256 indexed positionId,
        bool isLong,
        uint256 size,
        uint256 price,
        uint256 leverage,
        uint256 fee
    );
    event PositionClosed(
        address indexed user,
        uint256 indexed positionId,
        uint256 size,
        uint256 price,
        int256 pnl,
        uint256 fee
    );
    event PositionIncreased(
        address indexed user,
        uint256 indexed positionId,
        uint256 sizeAdded,
        uint256 newSize,
        uint256 newEntryPrice,
        uint256 fee
    );
    event FundingUpdated(
        int256 fundingRate,
        uint256 fundingIndex,
        int256 premiumFraction
    );
    event FundingPaid(
        address indexed user,
        uint256 indexed positionId,
        int256 amount,
        uint256 fundingIndex
    );
    event PositionLiquidated(
        address indexed user,
        uint256 indexed positionId,
        address indexed liquidator,
        uint256 size,
        uint256 price,
        uint256 fee
    );
    event TradingFeeCollected(address indexed user, uint256 amount);
    event ParametersUpdated(string parameter, uint256 newValue);
    event AuthorizedAdded(address indexed account);
    event AuthorizedRemoved(address indexed account);
    event Paused();
    event Unpaused();
    event BondingCurveUpdated(uint256 newPrice, uint256 totalSupply, uint256 priceChange);

    modifier onlyOwner() {
        require(msg.sender == owner, "vAMM: not owner");
        _;
    }

    modifier onlyAuthorized() {
        require(authorized[msg.sender] || msg.sender == owner, "vAMM: not authorized");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "vAMM: paused");
        _;
    }

    modifier validUser(address user) {
        require(user != address(0), "vAMM: invalid user");
        _;
    }

    modifier validPosition(uint256 positionId) {
        require(positions[positionId].isActive, "vAMM: position not active");
        _;
    }

    modifier onlyPositionOwner(uint256 positionId) {
        require(positionOwner[positionId] == msg.sender, "vAMM: not position owner");
        _;
    }

    constructor(
        address _vault,
        address _oracle,
        uint256 _startingPrice
    ) {
        owner = msg.sender;
        vault = IVault(_vault);
        oracle = IPriceOracle(_oracle);
        startingPrice = _startingPrice;

        // Initialize legacy reserves for backwards compatibility
        _updateLegacyReserves();

        // Initialize funding state
        fundingState.lastFundingTime = block.timestamp;
        fundingState.fundingIndex = FUNDING_PRECISION;

        // Initialize global position ID
        globalPositionId = 1;
    }

    /**
     * @dev BONDING CURVE PRICE CALCULATION
     * Formula: price = startingPrice * (1 + totalSupply/steepness)^exponent
     * Early buys = cheap tokens, later buys = exponentially more expensive
     */
    function getMarkPrice() public view override returns (uint256) {
        uint256 totalSupply = getTotalSupply();

        if (totalSupply == 0) {
            return startingPrice;
        }

        // Calculate bonding curve price using PRBMath
        // Formula: price = startingPrice * (1 + supply/steepness)^exponent
        uint256 supplyRatio = totalSupply.mul(PRICE_PRECISION).div(BONDING_CURVE_STEEPNESS);
        uint256 base = PRICE_PRECISION.add(supplyRatio);
        uint256 priceMultiplier = base.pow(pumpExponent);

        // Cap maximum price to prevent overflow
        uint256 maxPrice = startingPrice * MAX_PUMP_MULTIPLIER;
        uint256 calculatedPrice = startingPrice.mul(priceMultiplier).div(PRICE_PRECISION);

        return calculatedPrice > maxPrice ? maxPrice : calculatedPrice;
    }

    /**
     * @dev Gets total supply for bonding curve (only long positions drive price up)
     * Shorts don't directly affect the bonding curve price
     */
    function getTotalSupply() public view returns (uint256) {
        return totalLongSize > 0 ? uint256(totalLongSize) : 0;
    }

    /**
     * @dev Updates legacy virtual reserves for backwards compatibility
     * Now calculated dynamically based on bonding curve
     */
    function _updateLegacyReserves() internal {
        uint256 currentPrice = getMarkPrice();
        uint256 totalSupply = getTotalSupply();

        // Create virtual reserves that would produce the current bonding curve price
        // This ensures backwards compatibility with systems expecting AMM-style reserves
        virtualBaseReserves = totalSupply > 0 ? totalSupply : PRICE_PRECISION;
        virtualQuoteReserves = (virtualBaseReserves * currentPrice) / PRICE_PRECISION;

        emit BondingCurveUpdated(currentPrice, totalSupply, 0);
    }

    /**
     * @dev Gets the price impact for a trade using bonding curve
     */
    function getPriceImpact(uint256 size, bool isLong) external view override returns (uint256) {
        if (!isLong) return 0; // Shorts don't impact bonding curve price

        uint256 currentPrice = getMarkPrice();
        uint256 currentSupply = getTotalSupply();
        uint256 newSupply = currentSupply + size;

        // Calculate new price with increased supply
        uint256 supplyRatio = newSupply.mul(PRICE_PRECISION).div(BONDING_CURVE_STEEPNESS);
        uint256 base = PRICE_PRECISION.add(supplyRatio);
        uint256 priceMultiplier = base.pow(pumpExponent);
        uint256 newPrice = startingPrice.mul(priceMultiplier).div(PRICE_PRECISION);

        // Cap maximum price
        uint256 maxPrice = startingPrice * MAX_PUMP_MULTIPLIER;
        newPrice = newPrice > maxPrice ? maxPrice : newPrice;

        return newPrice > currentPrice ? newPrice - currentPrice : 0;
    }

    /**
     * @dev Gets current effective reserves and multiplier info
     */
    function getReserveInfo() external view returns (
        uint256 baseReserves,
        uint256 quoteReserves,
        uint256 multiplier,
        uint256 totalVolume
    ) {
        // These values are no longer directly applicable to the bonding curve
        // as the reserves are now derived from the total supply.
        // For compatibility, we can return 0 or throw an error.
        // For now, returning 0 as a placeholder.
        baseReserves = 0;
        quoteReserves = 0;
        multiplier = 0;
        totalVolume = 0;
    }

    /**
     * @dev Gets base reserves (non-dynamic)
     */
    function getBaseReserves() external view returns (uint256 baseReserves, uint256 quoteReserves) {
        // These values are no longer directly applicable to the bonding curve
        // as the reserves are now derived from the total supply.
        // For compatibility, we can return 0 or throw an error.
        // For now, returning 0 as a placeholder.
        baseReserves = 0;
        quoteReserves = 0;
    }

    /**
     * @dev Updates dynamic reserves parameters (owner only)
     */
    function updateDynamicReservesParams(
        uint256 _volumeScaleFactor,
        uint256 _minReserveMultiplier,
        uint256 _maxReserveMultiplier
    ) external onlyOwner {
        // This function is no longer relevant for the bonding curve
        // as reserves are derived from total supply.
        // Keeping it for compatibility if other parts of the system rely on it.
        require(_volumeScaleFactor > 0, "vAMM: invalid volume scale factor");
        require(_minReserveMultiplier > 0, "vAMM: invalid min multiplier");
        require(_maxReserveMultiplier > _minReserveMultiplier, "vAMM: invalid max multiplier");

        // volumeScaleFactor = _volumeScaleFactor; // No longer used
        // minReserveMultiplier = _minReserveMultiplier; // No longer used
        // maxReserveMultiplier = _maxReserveMultiplier; // No longer used

        emit ParametersUpdated("dynamicReservesParams", _volumeScaleFactor);
    }

    /**
     * @dev Updates base virtual reserves (owner only)
     */
    function updateBaseVirtualReserves(
        uint256 _baseVirtualBaseReserves,
        uint256 _baseVirtualQuoteReserves
    ) external onlyOwner {
        // This function is no longer relevant for the bonding curve
        // as reserves are derived from total supply.
        // Keeping it for compatibility if other parts of the system rely on it.
        require(_baseVirtualBaseReserves > 0 && _baseVirtualQuoteReserves > 0, "vAMM: invalid base reserves");

        // baseVirtualBaseReserves = _baseVirtualBaseReserves; // No longer used
        // baseVirtualQuoteReserves = _baseVirtualQuoteReserves; // No longer used

        emit ParametersUpdated("baseVirtualReserves", _baseVirtualBaseReserves);
    }

    /**
     * @dev Emits position events manually (owner or authorized only)
     * Useful for external integrations or manual event emission
     */
    function emitPositionEvent(
        address user,
        uint256 positionId,
        bool isOpenEvent,
        bool isLong,
        uint256 size,
        uint256 price,
        uint256 leverageOrPnL,
        uint256 fee
    ) external onlyAuthorized {
        require(user != address(0), "vAMM: invalid user");

        if (isOpenEvent) {
            emit PositionOpened(user, positionId, isLong, size, price, leverageOrPnL, fee);
        } else {
            emit PositionClosed(user, positionId, size, price, int256(leverageOrPnL), fee);
        }
    }

    /**
     * @dev Adds an authorized address
     */
    function addAuthorized(address account) external onlyOwner {
        require(account != address(0), "vAMM: invalid address");
        authorized[account] = true;
        emit AuthorizedAdded(account);
    }

    /**
     * @dev Removes an authorized address
     */
    function removeAuthorized(address account) external onlyOwner {
        authorized[account] = false;
        emit AuthorizedRemoved(account);
    }

    /**
     * @dev Helper function to remove position from user's active positions
     */
    function _removePositionFromUser(address user, uint256 positionId) internal {
        uint256[] storage userPositions = userPositionIds[user];
        for (uint256 i = 0; i < userPositions.length; i++) {
            if (userPositions[i] == positionId) {
                userPositions[i] = userPositions[userPositions.length - 1];
                userPositions.pop();
                break;
            }
        }
    }

    /**
     * @dev Liquidates an undercollateralized position
     */
    function liquidate(uint256 positionId) external whenNotPaused validPosition(positionId) {
        address user = positionOwner[positionId];
        require(vault.canLiquidate(user, maintenanceMarginRatio), "vAMM: cannot liquidate");

        Position storage pos = positions[positionId];
        uint256 positionSize = uint256(pos.size);

        // Update funding before liquidation
        updateFunding();
        _applyFundingToPosition(positionId);

        // Calculate liquidation fee
        uint256 liquidationFee = (positionSize * liquidationFeeRate) / BASIS_POINTS;

        // Update position tracking
        if (pos.isLong) {
            totalLongSize -= int256(positionSize);
        } else {
            totalShortSize -= int256(positionSize);
        }

        // Update legacy reserves
        _updateLegacyReserves();

        // Close position
        uint256 currentPrice = getMarkPrice();
        pos.isActive = false;
        _removePositionFromUser(user, positionId);

        // Liquidate in vault
        vault.liquidate(user, liquidationFee);

        emit PositionLiquidated(user, positionId, msg.sender, positionSize, currentPrice, liquidationFee);
    }

    /**
     * @dev Updates funding rate and applies funding
     */
    function updateFunding() public override {
        if (block.timestamp < fundingState.lastFundingTime + FUNDING_INTERVAL) {
            return; // Not time for funding update yet
        }

        uint256 markPrice = getMarkPrice();
        uint256 indexPrice = oracle.getPrice();

        // Calculate premium fraction (mark price vs index price)
        int256 premiumFraction = int256((markPrice * FUNDING_PRECISION) / indexPrice) - int256(FUNDING_PRECISION);

        // Calculate funding rate (simplified implementation)
        // In production, this would use TWAP and more sophisticated calculation
        int256 fundingRate = premiumFraction / 24; // Divide by 24 for hourly rate

        // Cap funding rate
        if (fundingRate > int256(MAX_FUNDING_RATE)) {
            fundingRate = int256(MAX_FUNDING_RATE);
        } else if (fundingRate < -int256(MAX_FUNDING_RATE)) {
            fundingRate = -int256(MAX_FUNDING_RATE);
        }

        // Update funding state
        fundingState.fundingRate = fundingRate;
        fundingState.premiumFraction = premiumFraction;
        fundingState.lastFundingTime = block.timestamp;

        // Update cumulative funding index
        fundingState.fundingIndex = uint256(int256(fundingState.fundingIndex) + fundingRate);

        emit FundingUpdated(fundingRate, fundingState.fundingIndex, premiumFraction);
    }

    /**
     * @dev Applies funding to a specific position
     */
    function _applyFundingToPosition(uint256 positionId) internal {
        Position storage pos = positions[positionId];
        if (!pos.isActive || pos.size == 0) return;

        uint256 fundingIndexDelta = fundingState.fundingIndex - pos.entryFundingIndex;
        if (fundingIndexDelta == 0) return;

        // Calculate funding payment
        uint256 positionSize = uint256(pos.size);

        int256 fundingPayment = int256(positionSize * fundingIndexDelta / FUNDING_PRECISION);
        if (pos.isLong) {
            fundingPayment = -fundingPayment; // Longs pay positive funding
        }

        // Apply funding in vault
        address user = positionOwner[positionId];
        vault.applyFunding(user, fundingPayment, fundingState.fundingIndex);

        // Update position funding index
        pos.entryFundingIndex = fundingState.fundingIndex;

        emit FundingPaid(user, positionId, fundingPayment, fundingState.fundingIndex);
    }

    /**
     * @dev Gets current funding rate
     */
    function getFundingRate() external view override returns (int256) {
        return fundingState.fundingRate;
    }

    /**
     * @dev Gets funding state
     */
    function getFundingState() external view override returns (FundingState memory) {
        return fundingState;
    }



    /**
     * @dev Applies funding to all positions of a user (legacy function for compatibility)
     */
    function _applyFunding(address user) internal {
        uint256[] memory userPosIds = userPositionIds[user];
        for (uint256 i = 0; i < userPosIds.length; i++) {
            if (positions[userPosIds[i]].isActive) {
                _applyFundingToPosition(userPosIds[i]);
            }
        }
    }

    /**
     * @dev Gets position data by position ID
     */
    function getPosition(uint256 positionId) external view override returns (Position memory) {
        return positions[positionId];
    }

    /**
     * @dev Gets position data by user and position ID
     */
    function getUserPosition(address user, uint256 positionId) external view override returns (Position memory) {
        require(positionOwner[positionId] == user, "vAMM: not position owner");
        return positions[positionId];
    }

    /**
     * @dev Gets all active positions for a user
     */
    function getUserPositions(address user) external view override returns (Position[] memory) {
        uint256[] memory userPosIds = userPositionIds[user];
        uint256 activeCount = 0;

        // Count active positions
        for (uint256 i = 0; i < userPosIds.length; i++) {
            if (positions[userPosIds[i]].isActive) {
                activeCount++;
            }
        }

        // Create array of active positions
        Position[] memory activePositions = new Position[](activeCount);
        uint256 index = 0;
        for (uint256 i = 0; i < userPosIds.length; i++) {
            if (positions[userPosIds[i]].isActive) {
                activePositions[index] = positions[userPosIds[i]];
                index++;
            }
        }

        return activePositions;
    }

    /**
     * @dev Gets all active position IDs for a user
     */
    function getUserPositionIds(address user) external view override returns (uint256[] memory) {
        uint256[] memory userPosIds = userPositionIds[user];
        uint256 activeCount = 0;

        // Count active positions
        for (uint256 i = 0; i < userPosIds.length; i++) {
            if (positions[userPosIds[i]].isActive) {
                activeCount++;
            }
        }

        // Create array of active position IDs
        uint256[] memory activePositionIds = new uint256[](activeCount);
        uint256 index = 0;
        for (uint256 i = 0; i < userPosIds.length; i++) {
            if (positions[userPosIds[i]].isActive) {
                activePositionIds[index] = userPosIds[i];
                index++;
            }
        }

        return activePositionIds;
    }

    /**
     * @dev Calculates unrealized PnL for a specific position
     */
    function getUnrealizedPnL(uint256 positionId) external view override returns (int256) {
        Position storage pos = positions[positionId];
        if (!pos.isActive || pos.size == 0) return 0;

        uint256 currentPrice = getMarkPrice();
        uint256 positionSize = uint256(pos.size);

        if (pos.isLong) {
            return int256(positionSize * (currentPrice - pos.entryPrice) / PRICE_PRECISION);
        } else {
            return int256(positionSize * (pos.entryPrice - currentPrice) / PRICE_PRECISION);
        }
    }

    /**
     * @dev Calculates total unrealized PnL for a user across all positions
     */
    function getTotalUnrealizedPnL(address user) external view override returns (int256) {
        uint256[] memory userPosIds = userPositionIds[user];
        int256 totalPnL = 0;

        for (uint256 i = 0; i < userPosIds.length; i++) {
            if (positions[userPosIds[i]].isActive) {
                totalPnL += this.getUnrealizedPnL(userPosIds[i]);
            }
        }

        return totalPnL;
    }

    /**
     * @dev Gets user's trading summary
     */
    function getUserSummary(address user) external view override returns (
        uint256 totalLongSize,
        uint256 totalShortSize,
        int256 totalPnL,
        uint256 activePositionsCount
    ) {
        uint256[] memory userPosIds = userPositionIds[user];

        for (uint256 i = 0; i < userPosIds.length; i++) {
            Position storage pos = positions[userPosIds[i]];
            if (pos.isActive) {
                activePositionsCount++;
                totalPnL += this.getUnrealizedPnL(userPosIds[i]);

                if (pos.isLong) {
                    totalLongSize += uint256(pos.size);
                } else {
                    totalShortSize += uint256(pos.size);
                }
            }
        }

        return (totalLongSize, totalShortSize, totalPnL, activePositionsCount);
    }

    /**
     * @dev Transfers ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "vAMM: invalid owner");
        owner = newOwner;
    }

    /**
     * @dev Emergency withdrawal of trading fees
     */
    function withdrawTradingFees(uint256 amount) external onlyOwner {
        require(amount <= totalTradingFees, "vAMM: insufficient fees");
        totalTradingFees -= amount;
        // Implementation would transfer fees to owner
    }

    /**
     * @dev Updates trading parameters
     */
    function updateTradingFeeRate(uint256 _feeRate) external onlyOwner {
        require(_feeRate <= 1000, "vAMM: fee too high"); // Max 10%
        tradingFeeRate = _feeRate;
        emit ParametersUpdated("tradingFeeRate", _feeRate);
    }

    function updateMaintenanceMarginRatio(uint256 _ratio) external onlyOwner {
        require(_ratio >= 100 && _ratio <= 2000, "vAMM: invalid ratio"); // 1-20%
        maintenanceMarginRatio = _ratio;
        emit ParametersUpdated("maintenanceMarginRatio", _ratio);
    }

    /**
     * @dev Opens a new position
     */
    function openPosition(
        uint256 collateralAmount,
        bool isLong,
        uint256 leverage,
        uint256 minPrice,
        uint256 maxPrice
    ) external override whenNotPaused returns (uint256 positionId) {
        require(collateralAmount > 0, "vAMM: invalid collateral");
        require(leverage >= MIN_LEVERAGE && leverage <= MAX_LEVERAGE, "vAMM: invalid leverage");

        // Update funding before position change
        updateFunding();

        // Calculate position size
        uint256 positionSize = collateralAmount * leverage;

        // Get current mark price
        uint256 currentPrice = getMarkPrice();
        require(currentPrice >= minPrice && currentPrice <= maxPrice, "vAMM: price slippage");

        // Calculate trading fee
        uint256 tradingFee = (positionSize * tradingFeeRate) / BASIS_POINTS;
        uint256 totalCost = collateralAmount + tradingFee;

        // CRITICAL FIX: Convert 18-decimal amounts to 6-decimal USDC for vault operations
        // The vAMM operates in 18-decimal precision, but the vault uses USDC (6 decimals)
        uint256 totalCostUSDC = totalCost / 1e12; // Convert from 18 to 6 decimals

        // Reserve margin in vault (in USDC 6-decimal units)
        vault.reserveMargin(msg.sender, totalCostUSDC);

        // Create new position ID
        positionId = globalPositionId++;

        // Update position tracking (bonding curve will automatically adjust price)
        if (isLong) {
            totalLongSize += int256(positionSize);
        } else {
            totalShortSize += int256(positionSize);
        }

        // Update legacy reserves for backwards compatibility
        _updateLegacyReserves();

        // Create new position
        positions[positionId] = Position({
            positionId: positionId,
            size: int256(positionSize),
            isLong: isLong,
            entryPrice: currentPrice,
            entryFundingIndex: fundingState.fundingIndex,
            lastInteractionTime: block.timestamp,
            isActive: true
        });

        // Update user position tracking
        positionOwner[positionId] = msg.sender;
        userPositionIds[msg.sender].push(positionId);

        // Collect trading fee
        totalTradingFees += tradingFee;

        emit PositionOpened(msg.sender, positionId, isLong, positionSize, currentPrice, leverage, tradingFee);
        emit TradingFeeCollected(msg.sender, tradingFee);

        return positionId;
    }

    /**
     * @dev Adds to an existing position
     */
    function addToPosition(
        uint256 positionId,
        uint256 collateralAmount,
        uint256 leverage,
        uint256 minPrice,
        uint256 maxPrice
    ) external override whenNotPaused validPosition(positionId) onlyPositionOwner(positionId) returns (uint256 newSize) {
        require(collateralAmount > 0, "vAMM: invalid collateral");
        require(leverage >= MIN_LEVERAGE && leverage <= MAX_LEVERAGE, "vAMM: invalid leverage");

        Position storage pos = positions[positionId];

        // Update funding before position change
        updateFunding();
        _applyFundingToPosition(positionId);

        // Calculate additional position size
        uint256 additionalSize = collateralAmount * leverage;

        // Get current mark price
        uint256 currentPrice = getMarkPrice();
        require(currentPrice >= minPrice && currentPrice <= maxPrice, "vAMM: price slippage");

        // Calculate trading fee
        uint256 tradingFee = (additionalSize * tradingFeeRate) / BASIS_POINTS;
        uint256 totalCost = collateralAmount + tradingFee;

        // CRITICAL FIX: Convert 18-decimal amounts to 6-decimal USDC for vault operations
        uint256 totalCostUSDC = totalCost / 1e12; // Convert from 18 to 6 decimals

        // Reserve margin in vault (in USDC 6-decimal units)
        vault.reserveMargin(msg.sender, totalCostUSDC);

        // Update position tracking (affects bonding curve price)
        if (pos.isLong) {
            totalLongSize += int256(additionalSize);
        } else {
            totalShortSize += int256(additionalSize);
        }

        // Update legacy reserves for backwards compatibility
        _updateLegacyReserves();

        // Calculate new weighted average entry price
        uint256 existingNotional = uint256(pos.size) * pos.entryPrice / PRICE_PRECISION;
        uint256 newNotional = additionalSize * currentPrice / PRICE_PRECISION;
        uint256 totalNotional = existingNotional + newNotional;

        pos.entryPrice = (totalNotional * PRICE_PRECISION) / (uint256(pos.size) + additionalSize);
        pos.size = pos.size + int256(additionalSize);
        pos.lastInteractionTime = block.timestamp;

        newSize = uint256(pos.size);

        // Collect trading fee
        totalTradingFees += tradingFee;

        emit PositionIncreased(msg.sender, positionId, additionalSize, newSize, pos.entryPrice, tradingFee);
        emit TradingFeeCollected(msg.sender, tradingFee);

        return newSize;
    }

    /**
     * @dev Closes a position partially or fully
     */
    function closePosition(
        uint256 positionId,
        uint256 sizeToClose,
        uint256 minPrice,
        uint256 maxPrice
    ) external override whenNotPaused validPosition(positionId) onlyPositionOwner(positionId) returns (int256 pnl) {
        Position storage pos = positions[positionId];
        uint256 positionSize = uint256(pos.size);
        require(sizeToClose <= positionSize, "vAMM: invalid size");

        // Update funding before closing
        updateFunding();
        _applyFundingToPosition(positionId);

        // Get current mark price
        uint256 currentPrice = getMarkPrice();
        require(currentPrice >= minPrice && currentPrice <= maxPrice, "vAMM: price slippage");

        // Calculate PnL
        if (pos.isLong) {
            pnl = int256(sizeToClose * (currentPrice - pos.entryPrice) / PRICE_PRECISION);
        } else {
            pnl = int256(sizeToClose * (pos.entryPrice - currentPrice) / PRICE_PRECISION);
        }

        // Calculate trading fee
        uint256 tradingFee = (sizeToClose * tradingFeeRate) / BASIS_POINTS;

        // Update position tracking (affects bonding curve price)
        if (pos.isLong) {
            totalLongSize -= int256(sizeToClose);
        } else {
            totalShortSize -= int256(sizeToClose);
        }

        // Update legacy reserves for backwards compatibility
        _updateLegacyReserves();

        // Update position
        if (sizeToClose == positionSize) {
            // Full close - deactivate position
            pos.isActive = false;
            _removePositionFromUser(msg.sender, positionId);
        } else {
            // Partial close
            pos.size = pos.size - int256(sizeToClose);
            pos.lastInteractionTime = block.timestamp;
        }

        // Update vault
        vault.updatePnL(msg.sender, pnl - int256(tradingFee));

        // Release margin proportionally
        uint256 marginToRelease = (vault.getMarginAccount(msg.sender).reservedMargin * sizeToClose) / positionSize;
        vault.releaseMargin(msg.sender, marginToRelease);

        // Collect trading fee
        totalTradingFees += tradingFee;

        emit PositionClosed(msg.sender, positionId, sizeToClose, currentPrice, pnl, tradingFee);
        emit TradingFeeCollected(msg.sender, tradingFee);

        return pnl;
    }

    /**
     * @dev Pauses the contract
     */
    function pause() external override onlyOwner {
        paused = true;
        emit Paused();
    }

    /**
     * @dev Unpauses the contract
     */
    function unpause() external override onlyOwner {
        paused = false;
        emit Unpaused();
    }

    /**
     * @dev Gets position data by user address (legacy function)
     * This returns the first active position for backwards compatibility
     */
    function getPosition(address user) external view override returns (Position memory) {
        uint256[] memory userPosIds = userPositionIds[user];
        require(userPosIds.length > 0, "vAMM: no positions");

        // Return the first active position (for backwards compatibility)
        for (uint256 i = 0; i < userPosIds.length; i++) {
            if (positions[userPosIds[i]].isActive) {
                return positions[userPosIds[i]];
            }
        }

        revert("vAMM: no active position");
    }

    /**
     * @dev Gets unrealized PnL by user address (legacy function)
     * This returns the total unrealized PnL across all positions
     */
    function getUnrealizedPnL(address user) external view override returns (int256) {
        return this.getTotalUnrealizedPnL(user);
    }

    // ===== BONDING CURVE MANAGEMENT FUNCTIONS =====

    /**
     * @dev Updates bonding curve parameters (owner only)
     */
    function updateBondingCurveParams(
        uint256 _newSteepness,
        uint256 _newExponent
    ) external onlyOwner {
        require(_newSteepness > 0, "vAMM: invalid steepness");
        require(_newExponent > 0, "vAMM: invalid exponent");

        // Note: These are constants in the current implementation
        // To make them updatable, change them from constants to state variables
        emit ParametersUpdated("bondingCurveParams", _newSteepness);
    }

    /**
     * @dev Gets current bonding curve status
     */
    function getBondingCurveInfo() external view returns (
        uint256 currentPrice,
        uint256 startPrice,
        uint256 totalSupply,
        uint256 steepness,
        uint256 exponent,
        uint256 maxPrice
    ) {
        currentPrice = getMarkPrice();
        startPrice = startingPrice;
        totalSupply = getTotalSupply();
        steepness = BONDING_CURVE_STEEPNESS;
        exponent = pumpExponent;
        maxPrice = startingPrice * MAX_PUMP_MULTIPLIER;
    }

    /**
     * @dev Calculates the cost to buy a specific amount on the bonding curve
     */
    function calculateBuyCost(uint256 amount) external view returns (uint256 totalCost) {
        uint256 currentSupply = getTotalSupply();
        uint256 currentPrice = getMarkPrice();
        uint256 newSupply = currentSupply + amount;

        // Calculate new price after purchase
        uint256 supplyRatio = newSupply.mul(PRICE_PRECISION).div(BONDING_CURVE_STEEPNESS);
        uint256 base = PRICE_PRECISION.add(supplyRatio);
        uint256 priceMultiplier = base.pow(pumpExponent);
        uint256 newPrice = startingPrice.mul(priceMultiplier).div(PRICE_PRECISION);

        // Cap maximum price
        uint256 maxPrice = startingPrice * MAX_PUMP_MULTIPLIER;
        newPrice = newPrice > maxPrice ? maxPrice : newPrice;

        // Calculate average price (simplified - in practice would need integration)
        uint256 averagePrice = (currentPrice + newPrice) / 2;
        totalCost = amount.mul(averagePrice).div(PRICE_PRECISION);
    }

    /**
     * @dev Calculates the payout for selling a specific amount on the bonding curve
     */
    function calculateSellPayout(uint256 amount) external view returns (uint256 totalPayout) {
        uint256 currentSupply = getTotalSupply();

        if (amount >= currentSupply) {
            // Can't sell more than total supply, return value for selling all
            amount = currentSupply;
        }

        uint256 currentPrice = getMarkPrice();
        uint256 newSupply = currentSupply - amount;

        // Calculate new price after sale
        uint256 newPrice;
        if (newSupply == 0) {
            newPrice = startingPrice;
        } else {
            uint256 supplyRatio = newSupply.mul(PRICE_PRECISION).div(BONDING_CURVE_STEEPNESS);
            uint256 base = PRICE_PRECISION.add(supplyRatio);
            uint256 priceMultiplier = base.pow(pumpExponent);
            newPrice = startingPrice.mul(priceMultiplier).div(PRICE_PRECISION);
        }

        // Calculate average price (simplified - in practice would need integration)
        uint256 averagePrice = (currentPrice + newPrice) / 2;
        totalPayout = amount.mul(averagePrice).div(PRICE_PRECISION);
    }

    /**
     * @dev Emergency function to reset bonding curve (owner only, extreme circumstances)
     */
    function emergencyResetBondingCurve(uint256 _newStartingPrice) external onlyOwner {
        require(paused, "vAMM: must be paused");
        require(_newStartingPrice > 0, "vAMM: invalid starting price");

        startingPrice = _newStartingPrice;
        totalLongSize = 0;
        totalShortSize = 0;

        _updateLegacyReserves();
        emit ParametersUpdated("emergencyReset", _newStartingPrice);
    }
}

contracts/vAMMFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./vAMM.sol";
import "./Vault.sol";

/**
 * @title vAMMFactory
 * @dev Factory contract for deploying BONDING CURVE vAMM instances with custom pricing
 * Now supports pump.fund-style markets with configurable starting prices
 */
contract vAMMFactory {
    address public owner;

    struct MarketInfo {
        address vamm;
        address vault;
        address oracle;
        address collateralToken;
        string symbol;
        bool isActive;
        uint256 createdAt;
        uint256 startingPrice; // Custom starting price for bonding curve
        MarketType marketType; // Type of market (pump, standard, blue-chip)
    }

    enum MarketType {
        PUMP,      // Ultra-pump style: $0.001 starting price
        STANDARD,  // Normal style: $1-10 starting price  
        BLUE_CHIP  // High-value style: $100+ starting price
    }

    mapping(bytes32 => MarketInfo) public markets;
    mapping(address => bool) public isValidMarket;
    bytes32[] public marketIds;

    uint256 public marketCount;
    uint256 public deploymentFee = 0.1 ether;

    // Predefined bonding curve templates
    mapping(MarketType => uint256) public defaultStartingPrices;

    event MarketCreated(
        bytes32 indexed marketId,
        string symbol,
        address indexed vamm,
        address indexed vault,
        address oracle,
        address collateralToken,
        uint256 startingPrice,
        MarketType marketType
    );

    event MarketStatusChanged(bytes32 indexed marketId, bool isActive);
    event DeploymentFeeUpdated(uint256 newFee);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event BondingCurveMarketCreated(
        bytes32 indexed marketId,
        string symbol,
        uint256 startingPrice,
        MarketType marketType,
        string description
    );

    event ContractDeployed(
        bytes32 indexed marketId,
        address indexed contractAddress,
        string contractType,
        bytes constructorArgs
    );

    modifier onlyOwner() {
        require(msg.sender == owner, "!owner");
        _;
    }

    modifier validMarketId(bytes32 marketId) {
        require(markets[marketId].vamm != address(0), "!found");
        _;
    }

    constructor() {
        owner = msg.sender;

        // Set default starting prices for different market types
        defaultStartingPrices[MarketType.PUMP] = 1e15;     // $0.001 (maximum pump potential)
        defaultStartingPrices[MarketType.STANDARD] = 1e18;  // $1.00 (balanced)
        defaultStartingPrices[MarketType.BLUE_CHIP] = 100e18; // $100.00 (stable/premium)
    }

    /**
     * @dev Creates a new bonding curve vAMM market with custom starting price
     */
    function createMarket(
        string memory symbol,
        address oracle,
        address collateralToken,
        uint256 startingPrice
    ) external payable returns (bytes32 marketId, address vammAddress, address vaultAddress) {
        require(msg.value >= deploymentFee, "!fee");
        require(oracle != address(0), "!oracle");
        require(collateralToken != address(0), "!collateral");
        require(bytes(symbol).length > 0, "!symbol");
        require(startingPrice > 0, "!price");

        // Determine market type based on starting price
        MarketType marketType = _determineMarketType(startingPrice);

        return _createMarketInternal(symbol, oracle, collateralToken, startingPrice, marketType);
    }



    /**
     * @dev Internal function to create markets
     */
    function _createMarketInternal(
        string memory symbol,
        address oracle,
        address collateralToken,
        uint256 startingPrice,
        MarketType marketType
    ) internal returns (bytes32 marketId, address vammAddress, address vaultAddress) {
        require(oracle != address(0), "!oracle");
        require(collateralToken != address(0), "!collateral");
        require(bytes(symbol).length > 0, "!symbol");
        require(startingPrice > 0, "!price");

        // Generate unique market ID
        marketId = keccak256(abi.encodePacked(symbol, oracle, collateralToken, block.timestamp, marketCount));
        require(markets[marketId].vamm == address(0), "!exists");

        // Deploy contracts first
        Vault vault = new Vault(collateralToken);
        vaultAddress = address(vault);

        vAMM vamm = new vAMM(vaultAddress, oracle, startingPrice);
        vammAddress = address(vamm);

        // Configure contracts
        vault.setVamm(vammAddress);
        vault.transferOwnership(msg.sender);
        vamm.transferOwnership(msg.sender);

        // Update state after all external calls (effects)
        marketCount++;
        marketIds.push(marketId);
        isValidMarket[vammAddress] = true;

        // Store market info
        markets[marketId] = MarketInfo({
            vamm: vammAddress,
            vault: vaultAddress,
            oracle: oracle,
            collateralToken: collateralToken,
            symbol: symbol,
            isActive: true,
            createdAt: block.timestamp,
            startingPrice: startingPrice,
            marketType: marketType
        });

        // Emit events
        emit MarketCreated(marketId, symbol, vammAddress, vaultAddress, oracle, collateralToken, startingPrice, marketType);
        emit ContractDeployed(marketId, vaultAddress, "Vault", abi.encode(collateralToken));
        emit ContractDeployed(marketId, vammAddress, "BondingCurveVAMM", abi.encode(vaultAddress, oracle, startingPrice));

        // Emit special event for bonding curve markets
        emit BondingCurveMarketCreated(marketId, symbol, startingPrice, marketType, "");

        return (marketId, vammAddress, vaultAddress);
    }

    /**
     * @dev Determines market type based on starting price
     */
    function _determineMarketType(uint256 startingPrice) internal pure returns (MarketType) {
        if (startingPrice <= 1e16) { // <= $0.01
            return MarketType.PUMP;
        } else if (startingPrice <= 10e18) { // <= $10.00
            return MarketType.STANDARD;
        } else {
            return MarketType.BLUE_CHIP; // > $10.00
        }
    }



    /**
     * @dev Updates market status
     */

    function setMarketStatus(bytes32 marketId, bool isActive) external onlyOwner validMarketId(marketId) {
        markets[marketId].isActive = isActive;
        emit MarketStatusChanged(marketId, isActive);
    }

    /**
     * @dev Updates deployment fee
     */
    function setDeploymentFee(uint256 newFee) external onlyOwner {
        deploymentFee = newFee;
        emit DeploymentFeeUpdated(newFee);
    }

    /**
     * @dev Gets market info by ID
     */
    function getMarket(bytes32 marketId) external view returns (MarketInfo memory) {
        return markets[marketId];
    }





    /**
     * @dev Transfers ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "!owner");
        address previousOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(previousOwner, newOwner);
    }

    /**
     * @dev Withdraws deployment fees
     */
    function withdrawFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "!balance");

        (bool success, ) = payable(owner).call{value: balance}("");
        require(success, "!withdraw");
    }


}

contracts/Vault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IVault.sol";

interface IERC20 {
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function transfer(address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

/**
 * @title Vault
 * @dev Modular margin vault for the vAMM system
 */
contract Vault is IVault {
    address public owner;
    address public vamm;
    IERC20 public collateralToken;

    mapping(address => MarginAccount) private accounts;
    mapping(address => bool) public authorized;

    uint256 public constant LIQUIDATION_THRESHOLD = 5000; // 50% in basis points
    uint256 public constant BASIS_POINTS = 10000;

    bool public paused = false;

    // Events
    event CollateralDeposited(address indexed user, uint256 amount);
    event CollateralWithdrawn(address indexed user, uint256 amount);
    event MarginReserved(address indexed user, uint256 amount);
    event MarginReleased(address indexed user, uint256 amount);
    event PnLUpdated(address indexed user, int256 pnlDelta);
    event FundingApplied(address indexed user, int256 fundingPayment, uint256 fundingIndex);
    event UserLiquidated(address indexed user, uint256 penalty);
    event AuthorizedAdded(address indexed account);
    event AuthorizedRemoved(address indexed account);
    event VammUpdated(address indexed newVamm);
    event Paused();
    event Unpaused();

    modifier onlyOwner() {
        require(msg.sender == owner, "Vault: not owner");
        _;
    }

    modifier onlyAuthorized() {
        require(authorized[msg.sender] || msg.sender == owner, "Vault: not authorized");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "Vault: paused");
        _;
    }

    modifier onlyVamm() {
        require(msg.sender == vamm, "Vault: not vamm");
        _;
    }

    constructor(address _collateralToken) {
        owner = msg.sender;
        collateralToken = IERC20(_collateralToken);
    }

    /**
     * @dev Sets the vAMM contract address
     */
    function setVamm(address _vamm) external onlyOwner {
        require(_vamm != address(0), "Vault: invalid vamm");
        vamm = _vamm;
        authorized[_vamm] = true;
        emit VammUpdated(_vamm);
        emit AuthorizedAdded(_vamm);
    }

    /**
     * @dev Adds an authorized contract
     */
    function addAuthorized(address account) external onlyOwner {
        require(account != address(0), "Vault: invalid address");
        authorized[account] = true;
        emit AuthorizedAdded(account);
    }

    /**
     * @dev Removes an authorized contract
     */
    function removeAuthorized(address account) external onlyOwner {
        authorized[account] = false;
        emit AuthorizedRemoved(account);
    }

    /**
     * @dev Pauses the vault
     */
    function pause() external onlyOwner {
        paused = true;
        emit Paused();
    }

    /**
     * @dev Unpauses the vault
     */
    function unpause() external onlyOwner {
        paused = false;
        emit Unpaused();
    }

    /**
     * @dev Deposits collateral for a user
     */
    function depositCollateral(address user, uint256 amount) external override whenNotPaused {
        require(amount > 0, "Vault: invalid amount");
        require(user != address(0), "Vault: invalid user");

        require(
            collateralToken.transferFrom(msg.sender, address(this), amount),
            "Vault: transfer failed"
        );

        accounts[user].collateral += amount;
        emit CollateralDeposited(user, amount);
    }

    /**
     * @dev Withdraws collateral for a user
     */
    function withdrawCollateral(address user, uint256 amount) external override whenNotPaused {
        require(amount > 0, "Vault: invalid amount");
        require(user != address(0), "Vault: invalid user");

        // Only user or authorized contracts can withdraw
        require(msg.sender == user || authorized[msg.sender], "Vault: not authorized");

        MarginAccount storage account = accounts[user];
        require(account.collateral >= amount, "Vault: insufficient collateral");

        // Check if withdrawal would leave sufficient margin
        uint256 availableMargin = getAvailableMargin(user);
        require(availableMargin >= amount, "Vault: insufficient available margin");

        account.collateral -= amount;
        require(
            collateralToken.transfer(user, amount),
            "Vault: transfer failed"
        );

        emit CollateralWithdrawn(user, amount);
    }

    /**
     * @dev Reserves margin for a position
     */
    function reserveMargin(address user, uint256 amount) external override onlyAuthorized whenNotPaused {
        require(amount > 0, "Vault: invalid amount");
        require(user != address(0), "Vault: invalid user");

        MarginAccount storage account = accounts[user];
        uint256 availableMargin = getAvailableMargin(user);
        require(availableMargin >= amount, "Vault: insufficient margin");

        account.reservedMargin += amount;
        emit MarginReserved(user, amount);
    }

    /**
     * @dev Releases reserved margin
     */
    function releaseMargin(address user, uint256 amount) external override onlyAuthorized whenNotPaused {
        require(amount > 0, "Vault: invalid amount");
        require(user != address(0), "Vault: invalid user");

        MarginAccount storage account = accounts[user];
        require(account.reservedMargin >= amount, "Vault: insufficient reserved margin");

        account.reservedMargin -= amount;
        emit MarginReleased(user, amount);
    }

    /**
     * @dev Updates PnL for a user
     */
    function updatePnL(address user, int256 pnlDelta) external override onlyAuthorized {
        require(user != address(0), "Vault: invalid user");

        accounts[user].unrealizedPnL += pnlDelta;
        emit PnLUpdated(user, pnlDelta);
    }

    /**
     * @dev Applies funding payment
     */
    function applyFunding(address user, int256 fundingPayment, uint256 fundingIndex) external override onlyAuthorized {
        require(user != address(0), "Vault: invalid user");

        MarginAccount storage account = accounts[user];
        account.unrealizedPnL += fundingPayment;
        account.lastFundingIndex = fundingIndex;

        emit FundingApplied(user, fundingPayment, fundingIndex);
    }

    /**
     * @dev Gets available margin for a user
     */
    function getAvailableMargin(address user) public view override returns (uint256) {
        MarginAccount storage account = accounts[user];
        int256 totalMargin = getTotalMargin(user);

        if (totalMargin <= 0) return 0;

        uint256 totalMarginUint = uint256(totalMargin);
        if (totalMarginUint <= account.reservedMargin) return 0;

        return totalMarginUint - account.reservedMargin;
    }

    /**
     * @dev Gets total margin (collateral + unrealized PnL)
     */
    function getTotalMargin(address user) public view override returns (int256) {
        MarginAccount storage account = accounts[user];
        return int256(account.collateral) + account.unrealizedPnL;
    }

    /**
     * @dev Checks if user can be liquidated
     */
    function canLiquidate(address user, uint256 maintenanceMarginRatio) external view override returns (bool) {
        MarginAccount storage account = accounts[user];

        if (account.reservedMargin == 0) return false; // No position

        int256 totalMargin = getTotalMargin(user);
        if (totalMargin <= 0) return true;

        uint256 requiredMargin = (account.reservedMargin * maintenanceMarginRatio) / BASIS_POINTS;
        return uint256(totalMargin) < requiredMargin;
    }

    /**
     * @dev Liquidates a user's position
     */
    function liquidate(address user, uint256 liquidationPenalty) external override onlyAuthorized {
        MarginAccount storage account = accounts[user];

        // Apply penalty to collateral
        if (account.collateral >= liquidationPenalty) {
            account.collateral -= liquidationPenalty;
        } else {
            account.collateral = 0;
        }

        // Reset reserved margin and PnL
        account.reservedMargin = 0;
        account.unrealizedPnL = 0;

        emit UserLiquidated(user, liquidationPenalty);
    }

    /**
     * @dev Gets user's margin account
     */
    function getMarginAccount(address user) external view override returns (MarginAccount memory) {
        return accounts[user];
    }

    /**
     * @dev Transfers ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Vault: invalid owner");
        owner = newOwner;
    }

    /**
     * @dev Emergency withdrawal by owner
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        require(paused, "Vault: not paused");
        IERC20(token).transfer(owner, amount);
    }
}

