// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IvAMM
 * @dev Interface for the virtual Automated Market Maker
 */
interface IvAMM {
    struct Position {
        int256 size; // Positive = LONG, Negative = SHORT
        uint256 entryPrice;
        uint256 entryFundingIndex;
        uint256 lastInteractionTime;
    }

    struct FundingState {
        int256 fundingRate; // Current funding rate (per hour)
        uint256 fundingIndex; // Cumulative funding index
        uint256 lastFundingTime;
        int256 premiumFraction; // Premium/discount relative to index price
    }

    /**
     * @dev Opens or modifies a position
     */
    function openPosition(
        uint256 collateralAmount,
        bool isLong,
        uint256 leverage,
        uint256 minPrice,
        uint256 maxPrice
    ) external returns (uint256 positionSize);

    /**
     * @dev Closes a position partially or fully
     */
    function closePosition(
        uint256 sizeToClose,
        uint256 minPrice,
        uint256 maxPrice
    ) external returns (int256 pnl);

    /**
     * @dev Gets current mark price
     */
    function getMarkPrice() external view returns (uint256);

    /**
     * @dev Gets current funding rate
     */
    function getFundingRate() external view returns (int256);

    /**
     * @dev Updates funding rate
     */
    function updateFunding() external;

    /**
     * @dev Gets position data for a user
     */
    function getPosition(address user) external view returns (Position memory);

    /**
     * @dev Gets funding state
     */
    function getFundingState() external view returns (FundingState memory);

    /**
     * @dev Calculates unrealized PnL for a position
     */
    function getUnrealizedPnL(address user) external view returns (int256);

    /**
     * @dev Gets the price impact for a trade
     */
    function getPriceImpact(uint256 size, bool isLong) external view returns (uint256);

    /**
     * @dev Emergency pause trading
     */
    function pause() external;

    /**
     * @dev Resume trading
     */
    function unpause() external;
}

contracts/IVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IVault
 * @dev Interface for the margin vault contract
 */
interface IVault {
    struct MarginAccount {
        uint256 collateral;
        uint256 reservedMargin;
        int256 unrealizedPnL;
        uint256 lastFundingIndex;
    }

    /**
     * @dev Deposits collateral for a user
     */
    function depositCollateral(address user, uint256 amount) external;

    /**
     * @dev Withdraws collateral for a user
     */
    function withdrawCollateral(address user, uint256 amount) external;

    /**
     * @dev Reserves margin for a position
     */
    function reserveMargin(address user, uint256 amount) external;

    /**
     * @dev Releases reserved margin
     */
    function releaseMargin(address user, uint256 amount) external;

    /**
     * @dev Updates PnL for a user
     */
    function updatePnL(address user, int256 pnlDelta) external;

    /**
     * @dev Applies funding payment
     */
    function applyFunding(address user, int256 fundingPayment, uint256 fundingIndex) external;

    /**
     * @dev Gets available margin for a user
     */
    function getAvailableMargin(address user) external view returns (uint256);

    /**
     * @dev Gets total margin (collateral + unrealized PnL)
     */
    function getTotalMargin(address user) external view returns (int256);

    /**
     * @dev Checks if user can be liquidated
     */
    function canLiquidate(address user, uint256 maintenanceMarginRatio) external view returns (bool);

    /**
     * @dev Liquidates a user's position
     */
    function liquidate(address user, uint256 liquidationPenalty) external;

    /**
     * @dev Gets user's margin account
     */
    function getMarginAccount(address user) external view returns (MarginAccount memory);
}

contracts/MockPriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IPriceOracle.sol";

/**
 * @title MockPriceOracle
 * @dev Mock price oracle for testing and development
 */
contract MockPriceOracle is IPriceOracle {
    address public owner;
    uint256 private price;
    uint256 private lastUpdateTime;
    uint256 public maxPriceAge = 1 hours;
    bool public active = true;

    event PriceUpdated(uint256 newPrice, uint256 timestamp);
    event OracleStatusChanged(bool active);
    event MaxPriceAgeUpdated(uint256 newAge);

    modifier onlyOwner() {
        require(msg.sender == owner, "Oracle: not owner");
        _;
    }

    constructor(uint256 _initialPrice) {
        owner = msg.sender;
        price = _initialPrice;
        lastUpdateTime = block.timestamp;
    }

    /**
     * @dev Returns the current price with 18 decimals
     */
    function getPrice() external view override returns (uint256) {
        require(active, "Oracle: inactive");
        require(block.timestamp <= lastUpdateTime + maxPriceAge, "Oracle: price too old");
        return price;
    }

    /**
     * @dev Returns the current price with timestamp
     */
    function getPriceWithTimestamp() external view override returns (uint256, uint256) {
        require(active, "Oracle: inactive");
        return (price, lastUpdateTime);
    }

    /**
     * @dev Checks if the oracle is active and healthy
     */
    function isActive() external view override returns (bool) {
        return active && (block.timestamp <= lastUpdateTime + maxPriceAge);
    }

    /**
     * @dev Returns the maximum acceptable price age in seconds
     */
    function getMaxPriceAge() external view override returns (uint256) {
        return maxPriceAge;
    }

    /**
     * @dev Updates the price (admin only)
     */
    function updatePrice(uint256 _newPrice) external onlyOwner {
        require(_newPrice > 0, "Oracle: invalid price");
        price = _newPrice;
        lastUpdateTime = block.timestamp;
        emit PriceUpdated(_newPrice, block.timestamp);
    }

    /**
     * @dev Sets oracle active status
     */
    function setActive(bool _active) external onlyOwner {
        active = _active;
        emit OracleStatusChanged(_active);
    }

    /**
     * @dev Updates max price age
     */
    function setMaxPriceAge(uint256 _maxAge) external onlyOwner {
        require(_maxAge > 0, "Oracle: invalid age");
        maxPriceAge = _maxAge;
        emit MaxPriceAgeUpdated(_maxAge);
    }

    /**
     * @dev Simulates price movement (for testing)
     */

    function simulatePriceMovement(int256 changePercent) external onlyOwner {
        require(changePercent >= -5000 && changePercent <= 5000, "Oracle: change too large"); // Max 50% change

        int256 newPrice = int256(price) + (int256(price) * changePercent) / 10000;
        require(newPrice > 0, "Oracle: invalid result");

        price = uint256(newPrice);
        lastUpdateTime = block.timestamp;
        emit PriceUpdated(price, block.timestamp);
    }

    /**
     * @dev Transfers ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Oracle: invalid owner");
        owner = newOwner;
    }
}

contracts/MockUSDC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title MockUSDC
 * @dev Mock USDC token for testing vAMM system
 */
contract MockUSDC {
    string public name = "Mock USD Coin";
    string public symbol = "USDC";
    uint8 public decimals = 6; // USDC has 6 decimals
    uint256 public totalSupply;

    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;

    address public owner;

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event Mint(address indexed to, uint256 value);

    modifier onlyOwner() {
        require(msg.sender == owner, "USDC: not owner");
        _;
    }

    constructor(uint256 _initialSupply) {
        owner = msg.sender;
        totalSupply = _initialSupply * 10**decimals;
        balanceOf[msg.sender] = totalSupply;
        emit Transfer(address(0), msg.sender, totalSupply);
    }

    function transfer(address to, uint256 value) external returns (bool) {
        require(to != address(0), "USDC: invalid recipient");
        require(balanceOf[msg.sender] >= value, "USDC: insufficient balance");

        balanceOf[msg.sender] -= value;
        balanceOf[to] += value;
        emit Transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) external returns (bool) {
        require(to != address(0), "USDC: invalid recipient");
        require(balanceOf[from] >= value, "USDC: insufficient balance");
        require(allowance[from][msg.sender] >= value, "USDC: insufficient allowance");

        balanceOf[from] -= value;
        balanceOf[to] += value;
        allowance[from][msg.sender] -= value;

        emit Transfer(from, to, value);
        return true;
    }

    function approve(address spender, uint256 value) external returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function mint(address to, uint256 value) external onlyOwner {
        require(to != address(0), "USDC: invalid recipient");

        totalSupply += value;
        balanceOf[to] += value;
        emit Mint(to, value);
        emit Transfer(address(0), to, value);
    }

    function faucet(uint256 amount) external {
        require(amount <= 10000 * 10**decimals, "USDC: faucet limit exceeded"); // Max 10,000 USDC

        totalSupply += amount;
        balanceOf[msg.sender] += amount;
        emit Mint(msg.sender, amount);
        emit Transfer(address(0), msg.sender, amount);
    }

    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "USDC: invalid owner");
        owner = newOwner;
    }
}

contracts/vAMM.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IvAMM.sol";
import "./IVault.sol";
import "./IPriceOracle.sol";

/**
 * @title vAMM
 * @dev Production-ready virtual Automated Market Maker with funding rate mechanism
 */
contract vAMM is IvAMM {
    address public owner;
    IVault public vault;
    IPriceOracle public oracle;

    // Trading parameters
    uint256 public constant PRICE_PRECISION = 1e18;
    uint256 public constant FUNDING_PRECISION = 1e8;
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant MAX_LEVERAGE = 100;
    uint256 public constant MIN_LEVERAGE = 1;

    // Fee structure
    uint256 public tradingFeeRate = 30; // 0.3% in basis points
    uint256 public liquidationFeeRate = 500; // 5% in basis points
    uint256 public maintenanceMarginRatio = 500; // 5% in basis points
    uint256 public initialMarginRatio = 1000; // 10% in basis points

    // Virtual pool for price discovery
    uint256 public virtualBaseReserves = 1e6 * PRICE_PRECISION;
    uint256 public virtualQuoteReserves = 1e6 * PRICE_PRECISION;

    // Funding mechanism
    FundingState public fundingState;
    uint256 public constant FUNDING_INTERVAL = 1 hours;
    uint256 public constant MAX_FUNDING_RATE = 1e6; // 1% per hour in FUNDING_PRECISION

    // Position tracking
    mapping(address => Position) public positions;
    mapping(address => bool) public authorized;

    // Global state
    int256 public totalLongSize;
    int256 public totalShortSize;
    uint256 public totalTradingFees;
    bool public paused = false;

    // Events
    event PositionOpened(
        address indexed user,
        bool isLong,
        uint256 size,
        uint256 price,
        uint256 leverage,
        uint256 fee
    );
    event PositionClosed(
        address indexed user,
        uint256 size,
        uint256 price,
        int256 pnl,
        uint256 fee
    );
    event FundingUpdated(
        int256 fundingRate,
        uint256 fundingIndex,
        int256 premiumFraction
    );
    event FundingPaid(
        address indexed user,
        int256 amount,
        uint256 fundingIndex
    );
    event PositionLiquidated(
        address indexed user,
        address indexed liquidator,
        uint256 size,
        uint256 price,
        uint256 fee
    );
    event TradingFeeCollected(address indexed user, uint256 amount);
    event ParametersUpdated(string parameter, uint256 newValue);
    event AuthorizedAdded(address indexed account);
    event AuthorizedRemoved(address indexed account);
    event Paused();
    event Unpaused();

    modifier onlyOwner() {
        require(msg.sender == owner, "vAMM: not owner");
        _;
    }

    modifier onlyAuthorized() {
        require(authorized[msg.sender] || msg.sender == owner, "vAMM: not authorized");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "vAMM: paused");
        _;
    }

    modifier validUser(address user) {
        require(user != address(0), "vAMM: invalid user");
        _;
    }

    constructor(
        address _vault,
        address _oracle,
        uint256 _initialPrice
    ) {
        owner = msg.sender;
        vault = IVault(_vault);
        oracle = IPriceOracle(_oracle);

        // Initialize virtual reserves based on initial price
        virtualQuoteReserves = (_initialPrice * virtualBaseReserves) / PRICE_PRECISION;

        // Initialize funding state
        fundingState.lastFundingTime = block.timestamp;
        fundingState.fundingIndex = FUNDING_PRECISION;
    }

    /**
     * @dev Adds an authorized address
     */
    function addAuthorized(address account) external onlyOwner {
        require(account != address(0), "vAMM: invalid address");
        authorized[account] = true;
        emit AuthorizedAdded(account);
    }

    /**
     * @dev Removes an authorized address
     */
    function removeAuthorized(address account) external onlyOwner {
        authorized[account] = false;
        emit AuthorizedRemoved(account);
    }

    /**
     * @dev Pauses trading
     */
    function pause() external override onlyOwner {
        paused = true;
        emit Paused();
    }

    /**
     * @dev Resumes trading
     */
    function unpause() external override onlyOwner {
        paused = false;
        emit Unpaused();
    }

    /**
     * @dev Updates trading parameters
     */
    function updateTradingFeeRate(uint256 _feeRate) external onlyOwner {
        require(_feeRate <= 1000, "vAMM: fee too high"); // Max 10%
        tradingFeeRate = _feeRate;
        emit ParametersUpdated("tradingFeeRate", _feeRate);
    }

    function updateMaintenanceMarginRatio(uint256 _ratio) external onlyOwner {
        require(_ratio >= 100 && _ratio <= 2000, "vAMM: invalid ratio"); // 1-20%
        maintenanceMarginRatio = _ratio;
        emit ParametersUpdated("maintenanceMarginRatio", _ratio);
    }

    /**
     * @dev Opens or modifies a position
     */
    function openPosition(
        uint256 collateralAmount,
        bool isLong,
        uint256 leverage,
        uint256 minPrice,
        uint256 maxPrice
    ) external override whenNotPaused returns (uint256 positionSize) {
        require(collateralAmount > 0, "vAMM: invalid collateral");
        require(leverage >= MIN_LEVERAGE && leverage <= MAX_LEVERAGE, "vAMM: invalid leverage");

        // Update funding before position change
        updateFunding();

        // Calculate position size
        positionSize = collateralAmount * leverage;

        // Get current mark price
        uint256 currentPrice = getMarkPrice();
        require(currentPrice >= minPrice && currentPrice <= maxPrice, "vAMM: price slippage");

        // Calculate trading fee
        uint256 tradingFee = (positionSize * tradingFeeRate) / BASIS_POINTS;
        uint256 totalCost = collateralAmount + tradingFee;

        // Reserve margin in vault
        vault.reserveMargin(msg.sender, totalCost);

        // Apply funding to existing position if any
        Position storage pos = positions[msg.sender];
        if (pos.size != 0) {
            _applyFunding(msg.sender);
        }

        // Update virtual reserves
        if (isLong) {
            virtualQuoteReserves += positionSize;
            totalLongSize += int256(positionSize);
        } else {
            virtualBaseReserves += positionSize;
            totalShortSize += int256(positionSize);
        }

        // Update position
        if (pos.size == 0) {
            // New position
            pos.size = isLong ? int256(positionSize) : -int256(positionSize);
            pos.entryPrice = currentPrice;
            pos.entryFundingIndex = fundingState.fundingIndex;
        } else {
            // Existing position - calculate weighted average entry price
            bool existingIsLong = pos.size > 0;
            if (existingIsLong == isLong) {
                // Same direction - add to position
                uint256 existingNotional = uint256(existingIsLong ? pos.size : -pos.size) * pos.entryPrice / PRICE_PRECISION;
                uint256 newNotional = positionSize * currentPrice / PRICE_PRECISION;
                uint256 totalNotional = existingNotional + newNotional;

                pos.entryPrice = (totalNotional * PRICE_PRECISION) / uint256(existingIsLong ? pos.size + int256(positionSize) : -pos.size + int256(positionSize));
                pos.size = existingIsLong ? pos.size + int256(positionSize) : pos.size - int256(positionSize);
            } else {
                // Opposite direction - reduce or flip position
                if (uint256(existingIsLong ? pos.size : -pos.size) > positionSize) {
                    // Reduce position
                    pos.size = existingIsLong ? pos.size - int256(positionSize) : pos.size + int256(positionSize);
                } else {
                    // Flip position
                    uint256 remainingSize = positionSize - uint256(existingIsLong ? pos.size : -pos.size);
                    pos.size = isLong ? int256(remainingSize) : -int256(remainingSize);
                    pos.entryPrice = currentPrice;
                    pos.entryFundingIndex = fundingState.fundingIndex;
                }
            }
        }

        pos.lastInteractionTime = block.timestamp;

        // Collect trading fee
        totalTradingFees += tradingFee;

        emit PositionOpened(msg.sender, isLong, positionSize, currentPrice, leverage, tradingFee);
        emit TradingFeeCollected(msg.sender, tradingFee);

        return positionSize;
    }

    /**
     * @dev Closes a position partially or fully
     */
    function closePosition(
        uint256 sizeToClose,
        uint256 minPrice,
        uint256 maxPrice
    ) external override whenNotPaused returns (int256 pnl) {
        Position storage pos = positions[msg.sender];
        require(pos.size != 0, "vAMM: no position");

        bool isLong = pos.size > 0;
        uint256 positionSize = uint256(isLong ? pos.size : -pos.size);
        require(sizeToClose <= positionSize, "vAMM: invalid size");

        // Update funding before closing
        updateFunding();
        _applyFunding(msg.sender);

        // Get current mark price
        uint256 currentPrice = getMarkPrice();
        require(currentPrice >= minPrice && currentPrice <= maxPrice, "vAMM: price slippage");

        // Calculate PnL
        if (isLong) {
            pnl = int256(sizeToClose * (currentPrice - pos.entryPrice) / PRICE_PRECISION);
        } else {
            pnl = int256(sizeToClose * (pos.entryPrice - currentPrice) / PRICE_PRECISION);
        }

        // Calculate trading fee
        uint256 tradingFee = (sizeToClose * tradingFeeRate) / BASIS_POINTS;

        // Update virtual reserves
        if (isLong) {
            virtualQuoteReserves -= sizeToClose;
            totalLongSize -= int256(sizeToClose);
        } else {
            virtualBaseReserves -= sizeToClose;
            totalShortSize -= int256(sizeToClose);
        }

        // Update position
        if (sizeToClose == positionSize) {
            // Full close
            delete positions[msg.sender];
        } else {
            // Partial close
            pos.size = isLong ? pos.size - int256(sizeToClose) : pos.size + int256(sizeToClose);
            pos.lastInteractionTime = block.timestamp;
        }

        // Update vault
        vault.updatePnL(msg.sender, pnl - int256(tradingFee));

        // Release margin proportionally
        uint256 marginToRelease = (vault.getMarginAccount(msg.sender).reservedMargin * sizeToClose) / positionSize;
        vault.releaseMargin(msg.sender, marginToRelease);

        // Collect trading fee
        totalTradingFees += tradingFee;

        emit PositionClosed(msg.sender, sizeToClose, currentPrice, pnl, tradingFee);
        emit TradingFeeCollected(msg.sender, tradingFee);

        return pnl;
    }

    /**
     * @dev Liquidates an undercollateralized position
     */
    function liquidate(address user) external whenNotPaused validUser(user) {
        require(vault.canLiquidate(user, maintenanceMarginRatio), "vAMM: cannot liquidate");

        Position storage pos = positions[user];
        require(pos.size != 0, "vAMM: no position");

        bool isLong = pos.size > 0;
        uint256 positionSize = uint256(isLong ? pos.size : -pos.size);

        // Update funding before liquidation
        updateFunding();
        _applyFunding(user);

        // Calculate liquidation fee
        uint256 liquidationFee = (positionSize * liquidationFeeRate) / BASIS_POINTS;

        // Update virtual reserves
        if (isLong) {
            virtualQuoteReserves -= positionSize;
            totalLongSize -= int256(positionSize);
        } else {
            virtualBaseReserves -= positionSize;
            totalShortSize -= int256(positionSize);
        }

        // Close position
        uint256 currentPrice = getMarkPrice();
        delete positions[user];

        // Liquidate in vault
        vault.liquidate(user, liquidationFee);

        emit PositionLiquidated(user, msg.sender, positionSize, currentPrice, liquidationFee);
    }

    /**
     * @dev Updates funding rate and applies funding
     */
    function updateFunding() public override {
        if (block.timestamp < fundingState.lastFundingTime + FUNDING_INTERVAL) {
            return; // Not time for funding update yet
        }

        uint256 markPrice = getMarkPrice();
        uint256 indexPrice = oracle.getPrice();

        // Calculate premium fraction (mark price vs index price)
        int256 premiumFraction = int256((markPrice * FUNDING_PRECISION) / indexPrice) - int256(FUNDING_PRECISION);

        // Calculate funding rate (simplified implementation)
        // In production, this would use TWAP and more sophisticated calculation
        int256 fundingRate = premiumFraction / 24; // Divide by 24 for hourly rate

        // Cap funding rate
        if (fundingRate > int256(MAX_FUNDING_RATE)) {
            fundingRate = int256(MAX_FUNDING_RATE);
        } else if (fundingRate < -int256(MAX_FUNDING_RATE)) {
            fundingRate = -int256(MAX_FUNDING_RATE);
        }

        // Update funding state
        fundingState.fundingRate = fundingRate;
        fundingState.premiumFraction = premiumFraction;
        fundingState.lastFundingTime = block.timestamp;

        // Update cumulative funding index
        fundingState.fundingIndex = uint256(int256(fundingState.fundingIndex) + fundingRate);

        emit FundingUpdated(fundingRate, fundingState.fundingIndex, premiumFraction);
    }

    /**
     * @dev Applies funding to a user's position
     */
    function _applyFunding(address user) internal {
        Position storage pos = positions[user];
        if (pos.size == 0) return;

        uint256 fundingIndexDelta = fundingState.fundingIndex - pos.entryFundingIndex;
        if (fundingIndexDelta == 0) return;

        // Calculate funding payment
        // Longs pay when funding is positive, receive when negative
        // Shorts receive when funding is positive, pay when negative
        bool isLong = pos.size > 0;
        uint256 positionSize = uint256(isLong ? pos.size : -pos.size);

        int256 fundingPayment = int256(positionSize * fundingIndexDelta / FUNDING_PRECISION);
        if (isLong) {
            fundingPayment = -fundingPayment; // Longs pay positive funding
        }

        // Apply funding in vault
        vault.applyFunding(user, fundingPayment, fundingState.fundingIndex);

        // Update position funding index
        pos.entryFundingIndex = fundingState.fundingIndex;

        emit FundingPaid(user, fundingPayment, fundingState.fundingIndex);
    }

    /**
     * @dev Gets current mark price from virtual reserves
     */
    function getMarkPrice() public view override returns (uint256) {
        return (virtualQuoteReserves * PRICE_PRECISION) / virtualBaseReserves;
    }

    /**
     * @dev Gets current funding rate
     */
    function getFundingRate() external view override returns (int256) {
        return fundingState.fundingRate;
    }

    /**
     * @dev Gets position data for a user
     */
    function getPosition(address user) external view override returns (Position memory) {
        return positions[user];
    }

    /**
     * @dev Gets funding state
     */
    function getFundingState() external view override returns (FundingState memory) {
        return fundingState;
    }

    /**
     * @dev Calculates unrealized PnL for a position
     */
    function getUnrealizedPnL(address user) external view override returns (int256) {
        Position storage pos = positions[user];
        if (pos.size == 0) return 0;

        uint256 currentPrice = getMarkPrice();
        bool isLong = pos.size > 0;
        uint256 positionSize = uint256(isLong ? pos.size : -pos.size);

        if (isLong) {
            return int256(positionSize * (currentPrice - pos.entryPrice) / PRICE_PRECISION);
        } else {
            return int256(positionSize * (pos.entryPrice - currentPrice) / PRICE_PRECISION);
        }
    }

    /**
     * @dev Gets the price impact for a trade
     */
    function getPriceImpact(uint256 size, bool isLong) external view override returns (uint256) {
        uint256 currentPrice = getMarkPrice();
        uint256 newPrice;

        if (isLong) {
            uint256 newQuoteReserves = virtualQuoteReserves + size;
            newPrice = (newQuoteReserves * PRICE_PRECISION) / virtualBaseReserves;
        } else {
            uint256 newBaseReserves = virtualBaseReserves + size;
            newPrice = (virtualQuoteReserves * PRICE_PRECISION) / newBaseReserves;
        }

        return newPrice > currentPrice ? newPrice - currentPrice : currentPrice - newPrice;
    }

    /**
     * @dev Transfers ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "vAMM: invalid owner");
        owner = newOwner;
    }

    /**
     * @dev Emergency withdrawal of trading fees
     */
    function withdrawTradingFees(uint256 amount) external onlyOwner {
        require(amount <= totalTradingFees, "vAMM: insufficient fees");
        totalTradingFees -= amount;
        // Implementation would transfer fees to owner
    }
}

contracts/vAMMFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./vAMM.sol";
import "./Vault.sol";

/**
 * @title vAMMFactory
 * @dev Factory contract for deploying vAMM instances
 */
contract vAMMFactory {
    address public owner;

    struct MarketInfo {
        address vamm;
        address vault;
        address oracle;
        address collateralToken;
        string symbol;
        bool isActive;
        uint256 createdAt;
    }

    mapping(bytes32 => MarketInfo) public markets;
    mapping(address => bool) public isValidMarket;
    bytes32[] public marketIds;

    uint256 public marketCount;
    uint256 public deploymentFee = 0.1 ether;

    event MarketCreated(
        bytes32 indexed marketId,
        string symbol,
        address indexed vamm,
        address indexed vault,
        address oracle,
        address collateralToken
    );

    event MarketStatusChanged(bytes32 indexed marketId, bool isActive);
    event DeploymentFeeUpdated(uint256 newFee);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    modifier onlyOwner() {
        require(msg.sender == owner, "Factory: not owner");
        _;
    }

    modifier validMarketId(bytes32 marketId) {
        require(markets[marketId].vamm != address(0), "Factory: market not found");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    /**
     * @dev Creates a new vAMM market
     */
    function createMarket(
        string memory symbol,
        address oracle,
        address collateralToken,
        uint256 initialPrice
    ) external payable returns (bytes32 marketId, address vammAddress, address vaultAddress) {
        require(msg.value >= deploymentFee, "Factory: insufficient fee");
        require(oracle != address(0), "Factory: invalid oracle");
        require(collateralToken != address(0), "Factory: invalid collateral");
        require(bytes(symbol).length > 0, "Factory: invalid symbol");
        require(initialPrice > 0, "Factory: invalid price");

        // Generate unique market ID
        marketId = keccak256(abi.encodePacked(symbol, oracle, collateralToken, block.timestamp, marketCount));
        require(markets[marketId].vamm == address(0), "Factory: market exists");

        // Deploy vault first
        Vault vault = new Vault(collateralToken);
        vaultAddress = address(vault);

        // Deploy vAMM
        vAMM vamm = new vAMM(vaultAddress, oracle, initialPrice);
        vammAddress = address(vamm);

        // Set vAMM in vault
        vault.setVamm(vammAddress);

        // Transfer ownership to deployer
        vault.transferOwnership(msg.sender);
        vamm.transferOwnership(msg.sender);

        // Store market info
        markets[marketId] = MarketInfo({
            vamm: vammAddress,
            vault: vaultAddress,
            oracle: oracle,
            collateralToken: collateralToken,
            symbol: symbol,
            isActive: true,
            createdAt: block.timestamp
        });

        isValidMarket[vammAddress] = true;
        marketIds.push(marketId);
        marketCount++;

        emit MarketCreated(marketId, symbol, vammAddress, vaultAddress, oracle, collateralToken);

        return (marketId, vammAddress, vaultAddress);
    }

    /**
     * @dev Updates market status
     */

    function setMarketStatus(bytes32 marketId, bool isActive) external onlyOwner validMarketId(marketId) {
        markets[marketId].isActive = isActive;
        emit MarketStatusChanged(marketId, isActive);
    }

    /**
     * @dev Updates deployment fee
     */
    function setDeploymentFee(uint256 newFee) external onlyOwner {
        deploymentFee = newFee;
        emit DeploymentFeeUpdated(newFee);
    }

    /**
     * @dev Gets market info by ID
     */
    function getMarket(bytes32 marketId) external view returns (MarketInfo memory) {
        return markets[marketId];
    }

    /**
     * @dev Gets all market IDs
     */
    function getAllMarketIds() external view returns (bytes32[] memory) {
        return marketIds;
    }

    /**
     * @dev Gets active markets
     */
    function getActiveMarkets() external view returns (bytes32[] memory activeMarkets) {
        uint256 activeCount = 0;

        // Count active markets
        for (uint256 i = 0; i < marketIds.length; i++) {
            if (markets[marketIds[i]].isActive) {
                activeCount++;
            }
        }

        // Create array of active market IDs
        activeMarkets = new bytes32[](activeCount);
        uint256 index = 0;
        for (uint256 i = 0; i < marketIds.length; i++) {
            if (markets[marketIds[i]].isActive) {
                activeMarkets[index] = marketIds[i];
                index++;
            }
        }

        return activeMarkets;
    }

    /**
     * @dev Transfers ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Factory: invalid owner");
        address previousOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(previousOwner, newOwner);
    }

    /**
     * @dev Withdraws deployment fees
     */
    function withdrawFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "Factory: no fees to withdraw");

        (bool success, ) = payable(owner).call{value: balance}("");
        require(success, "Factory: withdrawal failed");
    }

    /**
     * @dev Emergency function to recover stuck tokens
     */
    function recoverToken(address token, uint256 amount) external onlyOwner {
        // The IERC20 interface is already imported, so we can use it directly
        // IERC20(token).transfer(owner, amount); // This line is removed as per the edit hint
    }
}

contracts/Vault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IVault.sol";

interface IERC20 {
    function transferFrom(address from, address to, uint256 amount) external returns (bool);
    function transfer(address to, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}

/**
 * @title Vault
 * @dev Modular margin vault for the vAMM system
 */
contract Vault is IVault {
    address public owner;
    address public vamm;
    IERC20 public collateralToken;

    mapping(address => MarginAccount) private accounts;
    mapping(address => bool) public authorized;

    uint256 public constant LIQUIDATION_THRESHOLD = 5000; // 50% in basis points
    uint256 public constant BASIS_POINTS = 10000;

    bool public paused = false;

    // Events
    event CollateralDeposited(address indexed user, uint256 amount);
    event CollateralWithdrawn(address indexed user, uint256 amount);
    event MarginReserved(address indexed user, uint256 amount);
    event MarginReleased(address indexed user, uint256 amount);
    event PnLUpdated(address indexed user, int256 pnlDelta);
    event FundingApplied(address indexed user, int256 fundingPayment, uint256 fundingIndex);
    event UserLiquidated(address indexed user, uint256 penalty);
    event AuthorizedAdded(address indexed account);
    event AuthorizedRemoved(address indexed account);
    event VammUpdated(address indexed newVamm);
    event Paused();
    event Unpaused();

    modifier onlyOwner() {
        require(msg.sender == owner, "Vault: not owner");
        _;
    }

    modifier onlyAuthorized() {
        require(authorized[msg.sender] || msg.sender == owner, "Vault: not authorized");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "Vault: paused");
        _;
    }

    modifier onlyVamm() {
        require(msg.sender == vamm, "Vault: not vamm");
        _;
    }

    constructor(address _collateralToken) {
        owner = msg.sender;
        collateralToken = IERC20(_collateralToken);
    }

    /**
     * @dev Sets the vAMM contract address
     */
    function setVamm(address _vamm) external onlyOwner {
        require(_vamm != address(0), "Vault: invalid vamm");
        vamm = _vamm;
        authorized[_vamm] = true;
        emit VammUpdated(_vamm);
        emit AuthorizedAdded(_vamm);
    }

    /**
     * @dev Adds an authorized contract
     */
    function addAuthorized(address account) external onlyOwner {
        require(account != address(0), "Vault: invalid address");
        authorized[account] = true;
        emit AuthorizedAdded(account);
    }

    /**
     * @dev Removes an authorized contract
     */
    function removeAuthorized(address account) external onlyOwner {
        authorized[account] = false;
        emit AuthorizedRemoved(account);
    }

    /**
     * @dev Pauses the vault
     */
    function pause() external onlyOwner {
        paused = true;
        emit Paused();
    }

    /**
     * @dev Unpauses the vault
     */
    function unpause() external onlyOwner {
        paused = false;
        emit Unpaused();
    }

    /**
     * @dev Deposits collateral for a user
     */
    function depositCollateral(address user, uint256 amount) external override whenNotPaused {
        require(amount > 0, "Vault: invalid amount");
        require(user != address(0), "Vault: invalid user");

        // If called by user directly, transfer from user
        if (msg.sender == user) {
            require(
                collateralToken.transferFrom(user, address(this), amount),
                "Vault: transfer failed"
            );
        } else {
            // If called by authorized contract, assume tokens are already in vault
            require(authorized[msg.sender], "Vault: not authorized");
        }

        accounts[user].collateral += amount;
        emit CollateralDeposited(user, amount);
    }

    /**
     * @dev Withdraws collateral for a user
     */
    function withdrawCollateral(address user, uint256 amount) external override whenNotPaused {
        require(amount > 0, "Vault: invalid amount");
        require(user != address(0), "Vault: invalid user");

        // Only user or authorized contracts can withdraw
        require(msg.sender == user || authorized[msg.sender], "Vault: not authorized");

        MarginAccount storage account = accounts[user];
        require(account.collateral >= amount, "Vault: insufficient collateral");

        // Check if withdrawal would leave sufficient margin
        uint256 availableMargin = getAvailableMargin(user);
        require(availableMargin >= amount, "Vault: insufficient available margin");

        account.collateral -= amount;
        require(
            collateralToken.transfer(user, amount),
            "Vault: transfer failed"
        );

        emit CollateralWithdrawn(user, amount);
    }

    /**
     * @dev Reserves margin for a position
     */
    function reserveMargin(address user, uint256 amount) external override onlyAuthorized whenNotPaused {
        require(amount > 0, "Vault: invalid amount");
        require(user != address(0), "Vault: invalid user");

        MarginAccount storage account = accounts[user];
        uint256 availableMargin = getAvailableMargin(user);
        require(availableMargin >= amount, "Vault: insufficient margin");

        account.reservedMargin += amount;
        emit MarginReserved(user, amount);
    }

    /**
     * @dev Releases reserved margin
     */
    function releaseMargin(address user, uint256 amount) external override onlyAuthorized whenNotPaused {
        require(amount > 0, "Vault: invalid amount");
        require(user != address(0), "Vault: invalid user");

        MarginAccount storage account = accounts[user];
        require(account.reservedMargin >= amount, "Vault: insufficient reserved margin");

        account.reservedMargin -= amount;
        emit MarginReleased(user, amount);
    }

    /**
     * @dev Updates PnL for a user
     */
    function updatePnL(address user, int256 pnlDelta) external override onlyAuthorized {
        require(user != address(0), "Vault: invalid user");

        accounts[user].unrealizedPnL += pnlDelta;
        emit PnLUpdated(user, pnlDelta);
    }

    /**
     * @dev Applies funding payment
     */
    function applyFunding(address user, int256 fundingPayment, uint256 fundingIndex) external override onlyAuthorized {
        require(user != address(0), "Vault: invalid user");

        MarginAccount storage account = accounts[user];
        account.unrealizedPnL += fundingPayment;
        account.lastFundingIndex = fundingIndex;

        emit FundingApplied(user, fundingPayment, fundingIndex);
    }

    /**
     * @dev Gets available margin for a user
     */
    function getAvailableMargin(address user) public view override returns (uint256) {
        MarginAccount storage account = accounts[user];
        int256 totalMargin = getTotalMargin(user);

        if (totalMargin <= 0) return 0;

        uint256 totalMarginUint = uint256(totalMargin);
        if (totalMarginUint <= account.reservedMargin) return 0;

        return totalMarginUint - account.reservedMargin;
    }

    /**
     * @dev Gets total margin (collateral + unrealized PnL)
     */
    function getTotalMargin(address user) public view override returns (int256) {
        MarginAccount storage account = accounts[user];
        return int256(account.collateral) + account.unrealizedPnL;
    }

    /**
     * @dev Checks if user can be liquidated
     */
    function canLiquidate(address user, uint256 maintenanceMarginRatio) external view override returns (bool) {
        MarginAccount storage account = accounts[user];

        if (account.reservedMargin == 0) return false; // No position

        int256 totalMargin = getTotalMargin(user);
        if (totalMargin <= 0) return true;

        uint256 requiredMargin = (account.reservedMargin * maintenanceMarginRatio) / BASIS_POINTS;
        return uint256(totalMargin) < requiredMargin;
    }

    /**
     * @dev Liquidates a user's position
     */
    function liquidate(address user, uint256 liquidationPenalty) external override onlyAuthorized {
        require(user != address(0), "Vault: invalid user");

        MarginAccount storage account = accounts[user];

        // Apply liquidation penalty
        if (liquidationPenalty > 0) {
            int256 penalty = int256(liquidationPenalty);
            account.unrealizedPnL -= penalty;
        }

        // Reset position-related data
        account.reservedMargin = 0;
        account.unrealizedPnL = 0;

        emit UserLiquidated(user, liquidationPenalty);
    }

    /**
     * @dev Gets user's margin account
     */
    function getMarginAccount(address user) external view override returns (MarginAccount memory) {
        return accounts[user];
    }

    /**
     * @dev Transfers ownership
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Vault: invalid owner");
        owner = newOwner;
    }

    /**
     * @dev Emergency withdrawal by owner
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        require(paused, "Vault: not paused");
        IERC20(token).transfer(owner, amount);
    }
}

